/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        dsViol.InsertRow(0);

        cbPerioNo  .Value = Arg_RacerPerioNo;
        mskDayNo   .Value = Arg_DayNo;
        calRaceDate.Value = Arg_Dt;

        fcSearchCandRace();
        return;
	}

    // 조회 후
	if( sServiceName == "d06010010-service:searchCandRace" ) {
		//GRID 생성의 시작....
        fcCreateObject();

        dsOutRound.ClearData();
        for ( var i = 1; i <= toInteger(dsOutCandRace.GetColumn(dsOutCandRace.currow, "ROUND_CNT")); i++ ) {
            dsOutRound.AddRow();
            dsOutRound.SetColumn(i - 1, "ROUND", i);
        }

        fcSearch();
        return;
	}

    // 조회 후
	if( sServiceName == "d06010011-service:searchCandRaceDetl" ) {

        for ( var i = 1; i <= toInteger(dsOutCandRace.GetColumn(dsOutCandRace.currow, "ROUND_CNT")); i++ ) {
            var nRow = fnc_GetExistValue(dsOutStnd, "ROUND", i);

            object("tbMain.tbOrgan.Div0.cboGbn_"      + i +"R").Value = dsOutStnd.GetColumn(nRow, "GBN"      );
            object("tbMain.tbOrgan.Div0.cboCnt_"      + i +"R").Value = dsOutStnd.GetColumn(nRow, "CCIT_CNT" );
            object("tbMain.tbOrgan.Div0.mskBoatTime_" + i +"R").Value = dsOutStnd.GetColumn(nRow, "BOAT_TIME");
        }
        // for ( var i = 1; i <= toInteger(dsOutCandRace.GetColumn(dsOutCandRace.currow, "ROUND_CNT")); i++ ) {
            // for ( var j = 1; j <= 4; j++ ) {
                // var oDataset = object("dsOutCandRaceDetl_"+i+"R_"+j+"T");
                // if ( oDataset.GetRowCount() < 6 ) {
                    // for ( var k = oDataset.GetRowCount(); k < 6; k++ ) {
                        // oDataset.AddRow();
                    // }
                // }
            // }
        // }

        g_oGrid = object("dsOutCandRaceDetl_1R_1T");
        g_oRow  = 0;

        if((dsCfm.GetColumn(0,"CFM_YN")) == "Y"){
			btnCfm.Enable = false;
			btnCfmCls.Enable = true;			
			divBtnLst.btnSave.Enable = false;	    //저장버튼 비활성화
		}
		else if((dsCfm.GetColumn(0,"CFM_YN")) == "N"){
			btnCfm.Enable = true;
			btnCfmCls.Enable = false;
			divBtnLst.btnSave.Enable = true;	    //저장버튼 활성화
		} else{		//오류상황
			btnCfm.Enable = false;
			btnCfmCls.Enable = false;
			divBtnLst.btnSave.Enable = false;	    //저장버튼 비활성화
		}
        return;
	}

    // 저장 후
	if( sServiceName == "d06010011-service:saveCandRaceDetl" ) {
		//fcSearchCandRace();
        return;
	}

	// 확정처리
	if( sServiceName == "d06010011-service:saveCfm" ) {
		fcSearchCandRace();
        return;
	}

    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

