}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcCreateObject() {
    var sTabType = "";

	sTabType += ' Border="Flat"        ';
	sTabType += ' BorderColor="user35" ';
	sTabType += ' Height="550"         ';
	sTabType += ' Id="tbMain"          ';
	sTabType += ' Left="6"             ';
	sTabType += ' TabOrder="10"        ';
	sTabType += ' Top="68"             ';
	sTabType += ' Width="1000"         ';

    Create("Tab", "tbMain", sTabType);

    var sContents = "";
    sContents += "<Contents>";
    sContents += GetObjectBaseContent(0);		//장비배정
    sContents += GetObjectBaseContent(1);		//출주배정 > 주선선수목록
    for ( var i = 1; i <= toInteger(dsOutCandRace.GetColumn(dsOutCandRace.currow, "ROUND_CNT")); i++ ) {
        //for ( var j = 1; j <= 4; j++ ) {
            sContents += GetObjectContent(i);	//출주배정
        //}
    }
    sContents += GetObjectTabEnd();

    sContents += GetObjectBaseContent(2);		//경주기록

    sContents += GetObjectBaseContent(3);		//경주기록

    sContents += "</Contents>";

    object("tbMain").contents = sContents;
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
>>  출주배정
+-------------------------------------------------------------------------------------------------*/
