function CreateDataset(nRound, nTeam) {
    var sDatasetType = 'OnColumnChanged=fcCandRecdChange ';

    Create("Dataset", "dsOutCandRaceDetl_" + nRound + "R_" + nTeam + "T", sDatasetType);
    dsObj = object("dsOutCandRaceDetl_" + nRound + "R_" + nTeam + "T");
    dsObj.AddColumn("CAND_NO" );
    dsObj.AddColumn("NM_KOR"  );
    dsObj.AddColumn("MOTOR_NO");
    dsObj.AddColumn("BOAT_NO" );
}
