sTabContents += '					<cell align="center" col="1" colid="CAND_NO" display="text"/>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       ';
sTabContents += '					<cell align="center" col="2" colid="NM_KOR" display="text"/>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ';
sTabContents += '					<cell align="center" col="3" colid="RACE_P" display="text" edit="number" limit="3" LimitDec="0"/>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   ';
sTabContents += '					<cell align="center" col="4" colid="RACE_2" display="text" edit="number" limit="3" LimitDec="0"/>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   ';
sTabContents += '					<cell align="center" col="5" colid="RACE_3" display="text" edit="number" limit="3" LimitDec="0"/>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   ';
sTabContents += '					<cell align="center" col="6" colid="RACE_S" display="text" edit="number" limit="3" LimitDec="0"/>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   ';
sTabContents += '					<cell align="center" col="7" colid="RACE_T" display="text" edit="number" limit="3" LimitDec="0"/>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   ';
sTabContents += '				</body>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 ';
sTabContents += '			</format>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   ';
sTabContents += '		</contents>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     ';
sTabContents += '	</Grid>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             ';
sTabContents += '</TabPage>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             ';

	return sTabContents; 
}



/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function GetObjectTabEnd() {
    var sTabContents = "";

	sTabContents += '                        </Contents> ';
	sTabContents += '	                 </Div> ';
    sTabContents += '                </TabPage> ';

    return sTabContents;
}

