}

function fcOrganCand(obj,nRow,nCell,nX,nY,nPivotIndex) {
    if ( g_oGrid == null ) return;
    if ( g_oRow  == null ) return;
    var sCandNo = dsOutCand.GetColumn(nRow, "CAND_NO");
    if ( fnc_IsNull(sCandNo) ) return;

    if ( fnc_GetExistValue(g_oGrid, "CAND_NO", sCandNo) > -1 ) {
        alert('이미 등록되어 있습니다.');
        return;
    }

    g_oGrid.SetColumn(g_oRow, "CAND_NO" , dsOutCand.GetColumn(nRow, "CAND_NO" ));
    g_oGrid.SetColumn(g_oRow, "NM_KOR"  , dsOutCand.GetColumn(nRow, "NM_KOR"  ));
    g_oGrid.SetColumn(g_oRow, "MOTOR_NO", dsOutCand.GetColumn(nRow, "MOTOR_NO"));
    g_oGrid.SetColumn(g_oRow, "BOAT_NO" , dsOutCand.GetColumn(nRow, "BOAT_NO" ));

    g_oRow++;

    if ( g_oRow > 5) {
        var objNm  = "dsOutCandRaceDetl_";
        var nRound = toInteger(substr(g_oGrid.id, length(objNm), length(g_oGrid.id) - length(objNm) - length("R_1k")));
        var nTeam  = toInteger(substr(g_oGrid.id, length(g_oGrid.id) - 2, 1));

        if ( nTeam == 4 ) {
            if ( nRound == toInteger(dsOutCandRace.GetColumn(dsOutCandRace.currow, "ROUND_CNT")) ) {
                g_oGrid = object(objNm + "1R_1T");
            } else {
                g_oGrid = object(objNm + (nRound + 1) + "R_1T");
            }
        } else {
            g_oGrid = object(substr(g_oGrid.id, 0, length(g_oGrid.id) - 2) + (nTeam + 1) + "T");
        }

        g_oRow = 0;
    }

    g_oGrid.row = g_oRow;
}

function fcSetOrganObject(obj,nRow,nCell,nX,nY,nPivotIndex) {
    g_oGrid = object(obj.BindDataset);
    g_oRow  = nRow;
}

