{
	//fcInit();
}

function fcChangeRound(obj,strCode,strText,nOldIndex,nNewIndex)
{
    //strCode -> Round
    if ( fnc_IsNull(strCode) ) {
        for ( var i = 1; i <= 4; i++ ) {
            object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").BindDataset = "";
        }

        tbMain.tbRaceRecd.edGbn      .Value = "";
        tbMain.tbRaceRecd.edCnt      .Value = "";
        tbMain.tbRaceRecd.mskBoatTime.Value = "";

        return;
    }

    for ( var i = 1; i <= 4; i++ ) {
        object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").BindDataset = "dsOutCandRaceDetl_" + strCode + "R_" + i + "T";
    }

    tbMain.tbRaceRecd.edGbn      .Value = object("tbMain.tbOrgan.Div0.cboGbn_"      + strCode +"R").Text;
    //tbMain.tbRaceRecd.edCnt      .Value = object("tbMain.tbOrgan.Div0.cboCnt_"      + strCode +"R").Value + "주회"; ==> 데이터를 사용하지 않음
    tbMain.tbRaceRecd.mskBoatTime.Value = object("tbMain.tbOrgan.Div0.mskBoatTime_" + strCode +"R").Value;

    // ST연습
    if ( object("tbMain.tbOrgan.Div0.cboGbn_"      + strCode +"R").Value == "005" ) {
        for ( var i = 1; i <= 4; i++ ) {
            for ( var j = 0; j < 9; j++ ) {
                if(j%3 == 1) 	{//코스
					object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", (j + 3), "edit", "combo");//2008.07.06 edit
					object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", (j + 3), "bkColor", "");
				} else			{//타임
					object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", (j + 3), "edit", "number");//2008.07.06 edit
					object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", (j + 3), "bkColor", "");
				}
            }

            //착순(입력불가)
            object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", 12, "edit", "none");
			object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", 12, "bkColor", "#D4D4D4");

			//전법(입력불가)
			object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", 13, "edit", "none");
			object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", 13, "bkColor", "#D4D4D4");
        }

        return;
    }

    // 모의경주
    if ( object("tbMain.tbOrgan.Div0.cboGbn_"      + strCode +"R").Value == "006" ) {
        for ( var i = 1; i <= 4; i++ ) {
            for ( var j = 0; j < 6; j++ ) {
				object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", (j + 3), "edit", "none");
				object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", (j + 3), "bkColor", "#D4D4D4");
			}
            for ( var j = 6; j < 9; j++ ) {
				if(j%3 == 1) 	{ //코스
					object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", (j + 3), "edit", "combo");
					object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", (j + 3), "bkColor", "");
				} else 			{ //타임
					object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", (j + 3), "edit", "number");
					object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", (j + 3), "bkColor", "");
				}
            }

            //착순(입력가능)
            object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", 12, "edit", "number");
			object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", 12, "bkColor", "");

			//전법(입력가능)
			object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", 13, "edit", "combo");
			object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", 13, "bkColor", "");
        }
        return;
    }

    // 기본선회, 2정선회, 3정선회, 편대항주, 기초과정
    if ( object("tbMain.tbOrgan.Div0.cboGbn_"      + strCode +"R").Value == "001" ||
		 object("tbMain.tbOrgan.Div0.cboGbn_"      + strCode +"R").Value == "002" ||
		 object("tbMain.tbOrgan.Div0.cboGbn_"      + strCode +"R").Value == "003" ||
		 object("tbMain.tbOrgan.Div0.cboGbn_"      + strCode +"R").Value == "004" ||
		 object("tbMain.tbOrgan.Div0.cboGbn_"      + strCode +"R").Value == "007"   ) {
        for ( var i = 1; i <= 4; i++ ) {
            for ( var j = 0; j < 9; j++ ) {//S/T 입력불가
				object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", (j + 3), "edit", "none");
				object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", (j + 3), "bkColor", "#D4D4D4");
			}
			//착순(입력불가)
            object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", 12, "edit", "none");
			object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", 12, "bkColor", "#D4D4D4");

			//전법(입력불가)
			object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", 13, "edit", "none");
			object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", 13, "bkColor", "#D4D4D4");
        }
        return;
    }
/*
    for ( var i = 1; i <= 4; i++ ) {
        for ( var j = 0; j < 6; j++ ) {
			if(j%2 == 0) 	{//코스
				object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", (j + 3), "edit", "number");//2008.07.06 edit
				object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", (j + 3), "bkColor", "#F2F2F2");
			} else			{//타임
				object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", (j + 3), "edit", "normal");//2008.07.06 edit
				object("tbMain.tbRaceRecd.grdCandRecd_" + i + "T").SetCellProp("body", (j + 3), "bkColor", "#F2F2F2");
			}
        }
    }
*/    

}

function fcGetEquip(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		object(obj.BindDataset).SetColumn(nRow,TRIM("CAND_NO      ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("NM_KOR       ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("MOTOR_NO     ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("BOAT_NO      ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_COURSE_1") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_FL_1  ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_TIME_1  ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_COURSE_2") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_FL_2  ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_TIME_2  ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_COURSE_3") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_FL_3  ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_TIME_3  ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_COURSE_4") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_FL_4  ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_TIME_4  ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_COURSE_5") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_FL_5  ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_TIME_5  ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_COURSE_6") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_FL_6  ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_TIME_6  ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_COURSE_7") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_FL_7  ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_TIME_7  ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("ARRIV_ORD    ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("STRATEGY     ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("VIOL         ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("VIOL_CNTNT   ") ,""); 
		object(obj.BindDataset).SetColumn(nRow,TRIM("RACE_CNTNT   ") ,""); 
	} 

	if ( nCell == 1 ) {
		var cd = "001";
		var arg = "grpcode="+Quote(cd) + " arg_title=모터선택";
		rtn = Dialog("form::D06000023_1.xml",arg,,,true,-1,-1);

		if( rtn == null || rtn == '' ) return;	

        if ( fnc_GetExistValue(object(obj.BindDataset), "MOTOR_NO", rtn[0]) >= 0 ) {
            fnc_Message(L_COM_ALT_0016, "N", "모터");
            return;
        }

		object(obj.BindDataset).SetColumn(nRow,"MOTOR_NO",rtn[0]); 
	} 

	if ( nCell == 2 ) {
		var cd = "002";
		var arg = "grpcode="+Quote(cd) + " arg_title=보트선택";
		rtn = Dialog("form::D06000023_1.xml",arg,,,true,-1,-1);

		if( rtn == null || rtn == '' ) return;

        if ( fnc_GetExistValue(object(obj.BindDataset), "BOAT_NO", rtn[0]) >= 0 ) {
            fnc_Message(L_COM_ALT_0016, "N", "보트");
            return;
        }

		object(obj.BindDataset).SetColumn(nRow,"BOAT_NO",rtn[0]);
	}
}	

function fcGetCnadEquip(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 2 ) {
		var cd = "001";
		var arg = "grpcode="+Quote(cd) + " arg_title=모터선택";
		rtn = Dialog("form::D06000023_1.xml",arg,,,true,-1,-1);

		if( rtn == null || rtn == '' ) return;	

        if ( fnc_GetExistValue(object(obj.BindDataset), "MOTOR_NO", rtn[0]) >= 0 ) {
            fnc_Message(L_COM_ALT_0016, "N", "모터");
            return;
        }

		object(obj.BindDataset).SetColumn(nRow,"MOTOR_NO",rtn[0]);
		fcSetCnadEquipAll(object(obj.BindDataset).GetColumn(nRow, "CAND_NO"), "MOTOR_NO", rtn[0]);
	} 

	if ( nCell == 3 ) {
		var cd = "002";
		var arg = "grpcode="+Quote(cd) + " arg_title=보트선택";
		rtn = Dialog("form::D06000023_1.xml",arg,,,true,-1,-1);

		if( rtn == null || rtn == '' ) return;

        if ( fnc_GetExistValue(object(obj.BindDataset), "BOAT_NO", rtn[0]) >= 0 ) {
            fnc_Message(L_COM_ALT_0016, "N", "보트");
            return;
        }

		object(obj.BindDataset).SetColumn(nRow,"BOAT_NO",rtn[0]);
		fcSetCnadEquipAll(object(obj.BindDataset).GetColumn(nRow, "CAND_NO"), "BOAT_NO", rtn[0]);
	}
}

function fcSetCnadEquipAll(sCandNo, sColumnNm, sValue) {
    for ( var i = 1; i <= toInteger(dsOutCandRace.GetColumn(dsOutCandRace.currow, "ROUND_CNT")); i++ ) {
        for ( var j = 1; j <= 4; j++ ) {
            var objDs = object("dsOutCandRaceDetl_" + i + "R_" + j + "T");
            var nRow = fnc_GetExistValue(objDs, "CAND_NO", sCandNo);
            if  ( nRow >= 0 ) objDs.SetColumn(nRow, sColumnNm, sValue);
        }
    }
}

function fcCandRecdChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "VIOL") {
		var cd="";
		var cdnm="";

		for (var i=0; i<dsViol.count; i++) {
			cd = dsViol.GetColumn(i,"CD");

			if ( quote(cd) == quote(varNewValue) ) {
				cdnm = dsViol.GetColumn(i,"PROV");
				obj.SetColumn(nRow, "VIOL_CNTNT", cdnm);
				break;
			}
		}
	}
}

function btnAutoOrgan_OnClick(obj)
{
	var nRacerCnt   = dsOutCand.GetRowCount();
	var nRow        = 0;
	var nRaceRegCnt = 6;
	var nMaxTeam    = Ceil(nRacerCnt / nRaceRegCnt);

	for ( var i = 1; i <= dsOutCandRace.GetColumn(dsOutCandRace.currow, "ROUND_CNT"); i++ ) {
        for ( var j = 1; j <= nMaxTeam; j++ ) {
            for ( var k = 0; k < nRaceRegCnt; k++ ) {
                object("dsOutCandRaceDetl_" + i + "R_" + j + "T").SetColumn(k, "CAND_NO" , "");
                object("dsOutCandRaceDetl_" + i + "R_" + j + "T").SetColumn(k, "NM_KOR"  , "");
                object("dsOutCandRaceDetl_" + i + "R_" + j + "T").SetColumn(k, "MOTOR_NO", "");
                object("dsOutCandRaceDetl_" + i + "R_" + j + "T").SetColumn(k, "BOAT_NO" , "");
            }
        }
	}

	for ( var i = 1; i <= dsOutCandRace.GetColumn(dsOutCandRace.currow, "ROUND_CNT"); i++ ) {
        for ( var j = 1; j <= nMaxTeam; j++ ) {
            for ( var k = 0; k < nRaceRegCnt; k++ ) {
                object("dsOutCandRaceDetl_" + i + "R_" + j + "T").SetColumn(k, "CAND_NO" , dsOutCand.GetColumn(nRow, "CAND_NO" ));
                object("dsOutCandRaceDetl_" + i + "R_" + j + "T").SetColumn(k, "NM_KOR"  , dsOutCand.GetColumn(nRow, "NM_KOR"  ));
                object("dsOutCandRaceDetl_" + i + "R_" + j + "T").SetColumn(k, "MOTOR_NO", dsOutCand.GetColumn(nRow, "MOTOR_NO"));
                object("dsOutCandRaceDetl_" + i + "R_" + j + "T").SetColumn(k, "BOAT_NO" , dsOutCand.GetColumn(nRow, "BOAT_NO" ));

                nRow++;
                if ( nRow == nRacerCnt ) nRow = 0;
            }
        }
	}
}
function fcPrint(){

	var logImgUrl = "";
	var sSubUrl   = "";   
	var sRptNm    = "";
	var sDataSet  = "";		
	var sArg      =  "";

   if(tbMain.tabIndex == 1){	//출주배정
		var nRound = dsOutCandRace.GetColumn(dsOutCandRace.currow, "ROUND_CNT");

		for(var k = 1;k<=nRound;k++){ // 경주 대상자 , 모터,보트
			var l = 0;
			for(var j = 1; j<=4;j++){
				for (var i = 1;i<=6;i++){
					if(k==1){dsOutPrint.AddRow();} 
					dsOutPrint.SetColumn(l, "CAND_NO_"+k,   object("dsOutCandRaceDetl_" + k + "R_" + j + "T").GetColumn(i-1, "CAND_NO"  ));
					dsOutPrint.SetColumn(l, "NM_KOR_"+k,    object("dsOutCandRaceDetl_" +  k + "R_" + j + "T").GetColumn(i-1, "NM_KOR"  ));
					dsOutPrint.SetColumn(l, "BOAT_NO_"+k,   object("dsOutCandRaceDetl_" + k + "R_" + j + "T").GetColumn(i-1, "BOAT_NO"  ));
					dsOutPrint.SetColumn(l, "MOTOR_NO_"+k,  object("dsOutCandRaceDetl_" +  k + "R_" + j + "T").GetColumn(i-1, "MOTOR_NO"  ));
					dsOutPrint.SetColumn(l, "TEAM_NO_"+k,   object("dsOutCandRaceDetl_" +  k + "R_" + j + "T").GetColumn(i-1, j  ));
					l++;
			}}}

		dsOutStndPrint.AddRow();	
		for(var k = 1;k<=nRound;k++){ //경주 정보 구분,시간, 주회수
			dsOutStndPrint.SetColumn(0,"GBN_"+k		 ,dsOutStnd.GetColumn(k-1,"GBN"));
			dsOutStndPrint.SetColumn(0,"BOAT_TIME_"+k,dsOutStnd.GetColumn(k-1,"BOAT_TIME"));
			dsOutStndPrint.SetColumn(0,"CCIT_CNT_"+k ,dsOutStnd.GetColumn(k-1,"CCIT_CNT"));
		}	

		logImgUrl = GBL_REPORT_URL+"log.jpg";
		sSubUrl   = "";   
	    sRptNm  = "d06010011-1.jrf";
		sDataSet  = " dsOutStndPrint=dsOutStndPrint dsOutPrint=dsOutPrint "; 
		sArg = "PerioNo="+cbPerioNo.Value +" Round="+mskDayNo.Text +" Date=" +calRaceDate.Value;
		sArg   += " Plc="  	   + dsOutCandRace.getcolumn(0,"PLC_CD_N");
		sArg   += " Weather="  + dsOutCandRace.getcolumn(0,"WEATHER_N");
        sArg   += " Wind_Dirc="+ dsOutCandRace.getcolumn(0,"WIND_DIRC_N"); 
        sArg   += " Wind_Spd=" + dsOutCandRace.getcolumn(0,"WIND_SPD");  
        sArg   += " Tempt="    + dsOutCandRace.getcolumn(0,"TEMPT");
		var sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)

		fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	}else if(tbMain.tabIndex == 2){		//경주결과

		var nRound = tbMain.tbRaceRecd.cboRound.value;

		var l = 0;			// 경주 대상자 , 모터,보트
		for(var j = 1; j<=4;j++){
			for (var i = 1;i<=6;i++){
				dsOutPrintRlt.AddRow();	
				dsOutPrintRlt.SetColumn(l, "CAND_NO"			,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "CAND_NO"  	));
				dsOutPrintRlt.SetColumn(l, "NM_KOR"				,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "NM_KOR"  		));
				dsOutPrintRlt.SetColumn(l, "MOTOR_NO"			,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "MOTOR_NO"  	));
				dsOutPrintRlt.SetColumn(l, "BOAT_NO"			,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "BOAT_NO"  	));
				dsOutPrintRlt.SetColumn(l, "RACE_COURSE_1"	,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_COURSE_1"));
				dsOutPrintRlt.SetColumn(l, "RACE_COURSE_2"	,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_COURSE_2"));
				dsOutPrintRlt.SetColumn(l, "RACE_COURSE_3"	,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_COURSE_3"));
				dsOutPrintRlt.SetColumn(l, "RACE_COURSE_4"	,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_COURSE_4"));
				dsOutPrintRlt.SetColumn(l, "RACE_COURSE_5"	,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_COURSE_5"));
				dsOutPrintRlt.SetColumn(l, "RACE_COURSE_6"	,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_COURSE_6"));
				dsOutPrintRlt.SetColumn(l, "RACE_TIME_1"		,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_TIME_1"  ));
				dsOutPrintRlt.SetColumn(l, "RACE_TIME_2"		,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_TIME_2"  ));
				dsOutPrintRlt.SetColumn(l, "RACE_TIME_3"		,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_TIME_3"  ));
				dsOutPrintRlt.SetColumn(l, "RACE_TIME_4"		,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_TIME_4"  ));
				dsOutPrintRlt.SetColumn(l, "RACE_TIME_5"		,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_TIME_5"  ));
				dsOutPrintRlt.SetColumn(l, "RACE_TIME_6"		,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_TIME_6"  ));
				dsOutPrintRlt.SetColumn(l, "RACE_FL_1"		,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_FL_1"  ));
				dsOutPrintRlt.SetColumn(l, "RACE_FL_2"		,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_FL_2"  ));
				dsOutPrintRlt.SetColumn(l, "RACE_FL_3"		,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_FL_3"  ));
				dsOutPrintRlt.SetColumn(l, "RACE_FL_4"		,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_FL_4"  ));
				dsOutPrintRlt.SetColumn(l, "RACE_FL_5"		,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_FL_5"  ));
				dsOutPrintRlt.SetColumn(l, "RACE_FL_6"		,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_FL_6"  ));
				dsOutPrintRlt.SetColumn(l, "RACE_FL_7"		,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_FL_7"  ));
				dsOutPrintRlt.SetColumn(l, "ARRIV_ORD"			,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "ARRIV_ORD"  	));
				dsOutPrintRlt.SetColumn(l, "STRATEGY"			,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "STRATEGY"  	));
				dsOutPrintRlt.SetColumn(l, "VIOL"				,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "VIOL"  		));
				dsOutPrintRlt.SetColumn(l, "RACE_CNTNT"		,  object("dsOutCandRaceDetl_" + nRound + "R_" + j + "T").GetColumn(i-1, "RACE_CNTNT"  	));
				l++;
			}
		}

		logImgUrl = GBL_REPORT_URL+"log.jpg";
		sSubUrl   = "";   
	    sRptNm  = "d06010011-2.jrf";
		sDataSet  = " dsOutPrintRlt=dsOutPrintRlt "; 
		sArg = "PerioNo="+cbPerioNo.Value +" Round="+mskDayNo.Text +" Date=" +calRaceDate.Value;
		sArg   += " Plc="  	   + dsOutCandRace.getcolumn(0,"PLC_CD_N");
		sArg   += " Weather="  + dsOutCandRace.getcolumn(0,"WEATHER_N");
        sArg   += " Wind_Dirc="+ dsOutCandRace.getcolumn(0,"WIND_DIRC_N"); 
        sArg   += " Wind_Spd=" + dsOutCandRace.getcolumn(0,"WIND_SPD");  
        sArg   += " Tempt="    + dsOutCandRace.getcolumn(0,"TEMPT");
        sArg   += " GBN_1="  	   + object("tbMain.tbOrgan.Div0.cboGbn_"      + nRound +"R").Text;
        sArg   += " CCIT_CNT_1="   + object("tbMain.tbOrgan.Div0.cboCnt_"      + nRound +"R").Value;
        sArg   += " BOAT_TIME_1="  + object("tbMain.tbOrgan.Div0.mskBoatTime_" + nRound +"R").Value;
		var sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)

		fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	}else{
		logImgUrl = GBL_REPORT_URL+"log.jpg";
		sSubUrl   = "";   
		sRptNm  = "d06010011-2.jrf";
		sDataSet  = " dsOutStndPrint=dsOutStndPrint dsOutPrint=dsOutPrint ";    //"UBI 쿼리명, MIP Dataset명"								
		sArg = "PerioNo ="+cbPerioNo.Value +" Round="+mskDayNo.Text +" Date=" +calRaceDate.Value;

		var sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)

		fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	}
}

function btnCfm_OnClick(obj)
{
	if ( !fcSearchCheck(obj) ) return;

	dsCfm.SetColumn(0,"CFM_YN","Y");

	var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

    sServiceName  = "d06010011-service:saveCfm";

    sInDataSet     = " dsCfm=dsCfm " ;
    sOutDataSet    = " dsCfm=dsCfm " ;

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

function btnCfmCls_OnClick(obj)
{
	if ( !fcSearchCheck(obj) ) return;

	dsCfm.SetColumn(0,"CFM_YN","N");

	var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

    sServiceName  = "d06010011-service:saveCfm";

    sInDataSet     = " dsCfm=dsCfm " ;
    sOutDataSet    = " dsCfm=dsCfm " ;

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");	
}

function fcOpenRaceViol(objGrd, nRow, nCell, strVal, nPivotIndex) {

    // Content로 넘겨지는 arguements 
    var sArg    = " Arg_RacerPerioNo="  + quote(cbPerioNo.value);
        sArg   += " Arg_DayNo="         + quote(mskDayNo.value);  
        sArg   += " Arg_Round="         + quote(tbMain.tbRaceRecd.cboRound.value);
        sArg   += " Arg_TeamNo="        + quote(object(objGrd.bindDataSet).getColumn(nRow,"TEAM_NO"));
        sArg   += " Arg_RaceRegNo="     + quote(object(objGrd.bindDataSet).getColumn(nRow,"RACE_REG_NO"));
        sArg   += " Arg_CandNo="        + quote(object(objGrd.bindDataSet).getColumn(nRow,"CAND_NO"));
        sArg   += " Arg_nRow="          + quote(nRow);
        sArg   += " Arg_nCell="         + quote(nCell);
        sArg   += " Arg_GridId="        + quote(objGrd.id);

    Dialog("form::D06010013.xml", sArg, 250, 250, "");
}


