/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsOutTrngAsk.ClearData();
    dsOutTrngAskRacer.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "d07000001-service:searchTrngAsk";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutTrngAsk=dsOutTrngAsk ";

	fcd_AddParam(Trim("STR_DT          "), calStrDt.Value       );
	fcd_AddParam(Trim("END_DT          "), calEndDt.Value       );

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchRacerCheck(obj) {
	return true;
}

function fcSearchRacer(obj) {
    var sServiceName = "d07000001-service:searchTrngAskRacer";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutTrngAskRacer=dsOutTrngAskRacer ";

    fcd_AddParam("TRNG_ASK_SEQ",    dsOutTrngAsk.GetColumn(dsOutTrngAsk.currow, "TRNG_ASK_SEQ"));
    fcd_AddParam("TRNG_ASK_STR_DT", dsOutTrngAsk.GetColumn(dsOutTrngAsk.currow, "TRNG_ASK_STR_DT"));    
    fcd_AddParam("TRNG_ASK_DD_NUM", dsOutTrngAsk.GetColumn(dsOutTrngAsk.currow, "TRNG_ASK_DD_NUM"));    

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function fcSearchRacerDetail(obj) {
    var sServiceName = "d07000001-service:searchTrngAskRacerDetail";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerDetail=dsOutRacerDetail ";

    fcd_AddParam("TRNG_STR_DT", dsOutTrngAsk     .GetColumn(dsOutTrngAsk     .currow, "TRNG_ASK_STR_DT"));
    fcd_AddParam("RACER_NO"   , dsOutTrngAskRacer.GetColumn(dsOutTrngAskRacer.currow, "RACER_NO"       ));

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutTrngAskRacer) )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "d07000001-service:SaveTrngAskRacer";
    var sInDataSet   = "dsOutTrngAskRacer=dsOutTrngAskRacer:u ";
    var sOutDataSet  = "";

	fcd_AddParam("JOB_TYPE",               "TRNG_FEE_UPDATE"    );
	fcd_AddParam(Trim("STR_DT          "), calStrDt.Value       );
	fcd_AddParam(Trim("END_DT          "), calEndDt.Value       );

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

