function dsOutTrngAsk_OnRowPosChanged(obj,nOldRow,nRow)
{
    dsOutTrngAskRacer.ClearData();
	fcSearchRacer(obj.id);
}

function dsOutTrngAskRacer_OnRowPosChanged(obj,nOldRow,nRow)
{
    if ( fnc_IsNull(dsOutTrngAskRacer.GetColumn(nRow, "RACER_NO")) ) return;
	fcSearchRacerDetail(obj.id);
}

function fcAceptAsk(obj)
{
	if (obj.id <> "btnAcept" && obj.id <> "btnAceptCancel") return;

	if (dsOutTrngAsk.FindRow("CHK","1") == -1) return;
	var bModified = false;
	for (var iRow=0;iRow<dsOutTrngAsk.rowcount();iRow++) {
		if (dsOutTrngAsk.GetColumn(iRow,"CHK") == "1") {
			if (obj.id == "btnAcept") {
				if (dsOutTrngAsk.GetColumn(iRow, "TRNG_ASK_STAT_CD") == "001") {
					dsOutTrngAsk.SetColumn(iRow, "TRNG_ASK_STAT_CD", "002");
					bModified = true;
				}	
			} else {
				if (dsOutTrngAsk.GetColumn(iRow, "TRNG_ASK_STAT_CD") == "002") {
					dsOutTrngAsk.SetColumn(iRow, "TRNG_ASK_STAT_CD", "001");
					bModified = true;
				}	
			}
		}
	}
	if (bModified == false) {
		fnc_message("변경할 자료가 없습니다.", "N");
	}

	var sServiceName = "d07000001-service:AceptTrngAsk";
    var sInDataSet   = "dsOutTrngAsk=dsOutTrngAsk:u ";
    var sOutDataSet  = "dsOutTrngAsk=dsOutTrngAsk ";

	fcd_AddParam("JOB_TYPE",               "ACEPT"              );
	fcd_AddParam(Trim("STR_DT          "), calStrDt.Value       );
	fcd_AddParam(Trim("END_DT          "), calEndDt.Value       );

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function dsOutTrngAskRacer_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "TRNG_FEE_PAY_YN") {
		if (varOldValue == "002" && varNewValue == "001") {
			if (Length(dsOutTrngAskRacer.GetColumn(nRow, "TRNG_FEE_PAY_MTHD")) == 0)
				dsOutTrngAskRacer.SetColumn(nRow, "TRNG_FEE_PAY_MTHD", "001");
		}
	} else if (strColumnID == "TRNG_FEE_PAY_MTHD") {
		if (varNewValue == "001" || varNewValue == "002") {
			dsOutTrngAskRacer.SetColumn(nRow, "TRNG_FEE_PAY_YN", "001");
		} else {
			dsOutTrngAskRacer.SetColumn(nRow, "TRNG_FEE_PAY_YN", "002");
		}
	}
}

function fcOnSize(obj,nCx,nCy,nState)
{
	fcSetResize(obj,nCx,nCy);
}

// 특정콤포넌트(그리드) 리사이즈
function fcSetResize(obj,nX,nY)
{	
	var nHeight = nY-440;
	if( nY <= 590) {
		nHeight = 150;
	}
	grdTrngAskRacer.Height = nHeight;
	Shape1.top = 372 + (nHeight - 150);
	Static2.top = 366 + (nHeight - 150);
	grdRacerDetl.top = 388 + (nHeight - 150);
	Grid0.top = 446 + (nHeight - 150);
	Grid1.top = 484 + (nHeight - 150);
	Grid2.top = 542 + (nHeight - 150);		
}
