function fcSaveCheck(obj) {

	//º¯°æµÈ ÀÚ·á°¡ ÀÖ´ÂÁö Ã¼Å©
	if ( !fnc_DatasetChangeCheck(dsOutTrngAskRacer) )
	{
		//ÀúÀå ÇÒ ÀÚ·á°¡ ¾øÀ½
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "d07000001-service:SaveTrngAskRacer";
    var sInDataSet   = "dsOutTrngAskRacer=dsOutTrngAskRacer:u ";
    var sOutDataSet  = "";

	fcd_AddParam("JOB_TYPE",               "TRNG_FEE_UPDATE"    );
	fcd_AddParam(Trim("STR_DT          "), calStrDt.Value       );
	fcd_AddParam(Trim("END_DT          "), calEndDt.Value       );

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

