/*-------------------------------------------------------------------------------------------------+
>>  Transaction ÈÄ Ã³¸® ÇØ¾ß ÇÒ ³»¿ë!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// °øÅëÄÚµå Á¶È¸½Ã
	if ( sServiceName == "common-service:searchCode" ) {
		fcSearch();
		return;
	}

	// ¿äÃ»¹øÈ£ Á¶È¸ ÈÄ
	if ( sServiceName == "d07000001-service:searchTrngAsk" ) {
        fnc_SetStatus(SNIS_CAN_0003, dsOutTrngAsk.GetRowCount());
		return;
	}

	// ¿äÃ»¹øÈ£º° ¼±¼ö Á¶È¸ ÈÄ
	if ( sServiceName == "d07000001-service:searchTrngAskRacer" ) {
        // ½ÂÀÎ±ÇÀÚ¸¸ ¼÷¹Úºñ ³³ºÎ¿©ºÎ, ³³ºÎ¹æ¹ýÀ» ¼öÁ¤ÇÒ ¼ö ÀÖ´Ù.
        if (sAprv == "T") {
			grdTrngAskRacer.SetCellProp("body", 6, "edit", "number");
			grdTrngAskRacer.SetCellProp("body", 7, "edit", "combo");
			grdTrngAskRacer.SetCellProp("body", 8, "edit", "combo");
        } else {
			grdTrngAskRacer.SetCellProp("body", 6, "edit", "none");
			grdTrngAskRacer.SetCellProp("body", 7, "edit", "none");
			grdTrngAskRacer.SetCellProp("body", 8, "edit", "none");
        }
	}

	// ÀÇ·Ú¼±¼öº° ¼¼ºÎ Á¶È¸ ÈÄ
	if ( sServiceName == "d07000001-service:searchTrngAskRacerDetail" ) {
        return;
	}

	if ( sServiceName == "d07000001-service:SaveTrngAskRacer") {
		fcSearch();
		return;
	}


	//¿À·ù°¡ ¾øÀÌ Á¤»ó Ã³¸®µÇ¾ú´Ù¸é...
	fcPostProcess(sServiceName);
}

