function fcInit(obj) {
    dsRacerTrngMst.ClearData();
}

function fcToExcel(obj) {
	var sExcelList = "";

	fcd_OpenExcelListExport(sExcelList);
}  

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);	

	if ( !fnc_IsMandatory(calStrDt, "시작일") ) return false;
	if ( !fnc_IsMandatory(calEndDt, "종료일") ) return false;

	return true;
}


//조회버튼
function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "d07000001-service:searchTrngAsk";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutTrngAsk=dsOutTrngAsk ";

	fcd_AddParam(Trim("STR_DT          "), calStrDt.Value       );
	fcd_AddParam(Trim("END_DT          "), calEndDt.Value       );

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

	fcSearchDailyAll(this);

}	    

function fcSearchDailyAll(obj) {

	var sServiceName = "d07000002-service:searchRacerTrngMst";
    var sInDataSet   = "";
    var sOutDataSet  = " dsRacerTrngMst=dsRacerTrngMst ";

	fcd_AddParam(Trim("STR_DT          "), calStrDt.Value       );
	fcd_AddParam(Trim("END_DT          "), calEndDt.Value       );


    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

/*-------------------------------------------------------------------------------------------------+
>>  삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	return true;
}

//저장버튼클릭
function fcSave(obj) {
    if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "d07000002-service:saveRacerTrngMst";
    var sInDataSet   = "dsRacerTrngMst=dsRacerTrngMst:u ";
    var sOutDataSet  = "dsRacerTrngMst=dsRacerTrngMst ";

	fcd_AddParam("STR_DT", Trim(calStrDt.Value));
	fcd_AddParam("END_DT", Trim(calEndDt.Value));
	fcd_AddParam(Trim("TRNG_ASK_SEQ    "), dsRacerTrngMst.GetColumn(dsRacerTrngMst.Row, "dsRacerTrngMst"));


    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}


/*-------------------------------------------------------------------------------------------------+
