function fcAddRow(obj) {
    var sArg = "Arg_sDt=" + quote("");
    Dialog("form::D07000004.xml", sArg, 834, 450, "");
    fcSearch(divSearch.btnSearch);
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
    if ( !fnc_DeleteCheck(dsRacerTrngMst, "CHK", "1") ) return;
}

/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        return;
	}
	if ( sServiceName == "d07000001-service:searchTrngAsk" ) {
		//dsOutTrngAsk.row = -1;
		return;
	}

	// 공통코드 조회시
	if ( sServiceName == "d07000002-service:searchRacerTrngMst" ) {
        dsRacerTrngMst.AddColumn("CHK");
        return;
	}

	// 공통코드 조회시
	if ( sServiceName == "d07000002-service:saveRacerTrngMst" ) {
        dsRacerTrngMst.AddColumn("CHK");
        return;
	}

    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {
}

function grdRacerTrngMst_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    var sDt = dsRacerTrngMst.GetColumn(nRow, "DT");
    var sTrngAskSeq = dsRacerTrngMst.GetColumn(nRow, "TRNG_ASK_SEQ");
    if ( fnc_IsNull(sDt) ) return;

    var sArg = "Arg_sDt=" + quote(sDt);
    sArg += " Arg_TrngAskSeq=" + quote(sTrngAskSeq);
    Dialog("form::D07000004.xml", sArg, 834, 450, "");
    fcSearch(divSearch.btnSearch);
}

//조회버튼
function fcSearchDaily(obj) {
	if (chkOnly.Value == 0) return;

	var sServiceName = "d07000002-service:searchAskRacerTrngMst";
    var sInDataSet   = "";
    var sOutDataSet  = " dsRacerTrngMst=dsRacerTrngMst ";

	fcd_AddParam("TRNG_ASK_SEQ", dsOutTrngAsk.GetColumn(dsOutTrngAsk.Row, "TRNG_ASK_SEQ"));

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}
