/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);	

	if ( !fnc_IsMandatory(calStrDt, "시작일") ) return false;
	if ( !fnc_IsMandatory(calEndDt, "종료일") ) return false;

	return true;
}


//조회버튼
function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "d07000007-service:searchTrngAskMentor";
    var sInDataSet   = "";
    var sOutDataSet  = "dsRacerTrngMentor=dsRacerTrngMentor dsMentee=dsMentee dsRacer=dsRacer";

	fcd_AddParam(Trim("STR_DT          "), calStrDt.Value       );
	fcd_AddParam(Trim("END_DT          "), calEndDt.Value       );

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}	    

function fcSearchDailyAll(obj) {

	var sServiceName = "d07000002-service:searchRacerTrngMentoring";
    var sInDataSet   = "";
    var sOutDataSet  = " dsRacerMentoring=dsRacerMentoring ";

	fcd_AddParam(Trim("STR_DT          "), calStrDt.Value       );
	fcd_AddParam(Trim("END_DT          "), calEndDt.Value       );


    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

