/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        return;
	}

	// 멘토 조회
	if ( sServiceName == "d07000007-service:searchRacerTrngMentor" ) {
       fnc_SetStatus(SNIS_CAN_0003, dsRacerTrngMentor.rowcount());
	}

	// 멘토링 세부내역
	if ( sServiceName == "d07000007-service:searchMentoring" ) {


	}
	if (sServiceName == "d07000007-service:saveRacerTrngMentoring") {
		fcSearch();
	}

    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}



//조회버튼
function fcSearchDaily(obj) {
	if (chkOnly.Value == 0) return;

	var sServiceName = "d07000007-service:searchMentoring";
    var sInDataSet   = "";
    var sOutDataSet  = " dsRacerMentoring=dsRacerMentoring ";

	fcd_AddParam("TRNG_ASK_SEQ", dsRacerTrngMentor.GetColumn(dsRacerTrngMentor.Row, "TRNG_ASK_SEQ"));
	fcd_AddParam("MENTO_GRP_NO", dsRacerTrngMentor.GetColumn(dsRacerTrngMentor.Row, "MENTO_GRP_NO"));

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}


function fcdsOnRowPosChanged(obj,nOldRow,nRow)
{
	if (obj.id == "dsRacerTrngMentor") {

		var filterStr = "";
		filterStr  = "TRNG_ASK_SEQ="+quote(dsRacerTrngMentor.GetColumn(nRow, "TRNG_ASK_SEQ"));
		filterStr += " && MENTO_GRP_NO="+quote(dsRacerTrngMentor.GetColumn(nRow, "MENTO_GRP_NO"));
		dsMentee.Filter(filterStr);

		filterStr = "";
		filterStr  = "TRNG_ASK_SEQ="+quote(dsRacerTrngMentor.GetColumn(nRow, "TRNG_ASK_SEQ"));
		dsRacer.Filter(filterStr);

		fcSearchDaily(obj);
	}
}

function fcdsOnColumnChnaged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (obj.id == "dsMentee") {
		dsRacerMentoring.SetColumn(dsRacerMentoring.row, "MENTEE_CNT", dsMentee.rowcount());
	}
}

function fcOnChanged(obj,strOldText,strNewText)
{
	if (dsRacerTrngMentor.row < 0) return;

	nRow = dsRacerTrngMentor.row;
	var StrDt  = dsRacerTrngMentor.GetColumn(nRow, "TRNG_ASK_STR_DT");
	var EndDt  = dsRacerTrngMentor.GetColumn(nRow, "TRNG_ASK_END_DT");

	if (strNewText < StrDt || strNewText > EndDt) {
		fnc_message("교육일자를 확인하세요!\n\n교육 가능기간:" + StrDt + "~" + EndDt + ")", "N");
		calTrngDt.Value = strOldText;
	}
}
