function fcSave(obj) {
    if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "d07000007-service:saveRacerTrngMentoring";
    var sInDataSet   = "dsRacerMentoring=dsRacerMentoring:u dsMentee=dsMentee:u ";
    var sOutDataSet  = "dsRacerMentoring=dsRacerMentoring ";

	//fcd_AddParam("STR_DT", Trim(calStrDt.Value));
	//fcd_AddParam("END_DT", Trim(calEndDt.Value));
	fcd_AddParam(Trim("TRNG_ASK_SEQ    "), dsRacerMentoring.GetColumn(dsRacerMentoring.Row, "TRNG_ASK_SEQ"));
	fcd_AddParam(Trim("MENTO_GRP_NO    "), dsRacerMentoring.GetColumn(dsRacerMentoring.Row, "MENTO_GRP_NO"));


    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}


/*-------------------------------------------------------------------------------------------------+
>>  ÀÎ¼â ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcPrint(){		

	// ÀÚ·á ÃÊ±âÈ­
	ds_reportParam.DeleteAll();
	var nRnum;

    // ÇÊµå °ªÀ» ¼ÂÆÃÇÑ´Ù.
    var paramField = [
							"TRNG_ASK_SEQ"						// ÈÆ·Ã¿äÃ»¹øÈ£
						,   "MENTO_GRP_NO"						// ¸àÅä±×·ì¹øÈ£
					 ];

	// °ªÀ» ¼ÂÆÃÇÑ´Ù.
	var paramValue = [
							dsRacerTrngMentor.GetColumn(dsRacerTrngMentor.Row, "TRNG_ASK_SEQ")
						,   dsRacerTrngMentor.GetColumn(dsRacerTrngMentor.Row, "MENTO_GRP_NO")
					 ];

	var rowCnt = paramField.length;	
	for(var row = 0 ; row < rowCnt; row++)
	{
		nRnum = ds_reportParam.AppendRow();
		ds_reportParam.SetColumn(nRnum, "PARAM_NM", paramField[row]);
		ds_reportParam.SetColumn(nRnum, "PARAM_VAL",paramValue[row]);
	}

	/* ·¹Æ÷Æ® ¹èÀ² Ãß°¡ - ½ÃÀÛ */
	iRow = ds_reportParam.AddRow();

	// Default Scale ÆÄ¶ó¹ÌÅÍ¸í Ãß°¡(°í??
	ds_reportParam.SetColumn(iRow, "PARAM_NM", "DEFAULT_SCALE");
	ds_reportParam.SetColumn(iRow, "PARAM_VAL", "1");

	var sRptFile = "d07000007";

	//Æ®·¢Àè¼ÇÀ» ÅÂ¿î´Ù.
	fnc_ReportPreView(sRptFile, ds_reportParam.ID, "Y");
}

