function fcAddRow(obj) {
    if (obj.GetForm().id == "divBtnRow") {
		if (dsRacerTrngMentor.row < 0) return;
		var nRow = dsRacerMentoring.AddRow();
		dsRacerMentoring.SetColumn(nRow, "TRNG_ASK_SEQ", dsRacerTrngMentor.GetColumn(dsRacerTrngMentor.Row, "TRNG_ASK_SEQ"));
		dsRacerMentoring.SetColumn(nRow, "MENTO_GRP_NO", dsRacerTrngMentor.GetColumn(dsRacerTrngMentor.Row, "MENTO_GRP_NO"));
		dsRacerMentoring.SetColumn(nRow, "TRNG_DT",      dsRacerTrngMentor.GetColumn(dsRacerTrngMentor.Row, "TRNG_ASK_STR_DT"));
		dsRacerMentoring.SetColumn(nRow, "MENTOR_NM",    dsRacerTrngMentor.GetColumn(dsRacerTrngMentor.Row, "MENTOR_NM"));
		dsRacerMentoring.SetColumn(nRow, "MENTEE_CNT",   dsRacerTrngMentor.GetColumn(dsRacerTrngMentor.Row, "MENTEE_CNT"));

    } else if (obj.GetForm().id == "divBtnRowRacer") {		
		if (dsRacerTrngMentor.row < 0) return;

		nRow = dsRacerTrngMentor.row;
		var StrDt  = dsRacerTrngMentor.GetColumn(nRow, "TRNG_ASK_STR_DT");
		var EndDt  = dsRacerTrngMentor.GetColumn(nRow, "TRNG_ASK_END_DT");
		var GrpNo  = dsRacerTrngMentor.GetColumn(nRow, "MENTO_GRP_NO");
		var AskSeq = dsRacerTrngMentor.GetColumn(nRow, "TRNG_ASK_SEQ");
		var Mento  = dsRacerTrngMentor.GetColumn(nRow, "MENTO_RACER_NO");
		if ( fnc_IsNull(StrDt) ) return;

		var sArg  = "ARG_STR_DT="  + quote(StrDt);
		    sArg += " ARG_END_DT=" + quote(EndDt);
		    sArg += " MENTO_GRP_NO="     + quote(GrpNo);
		    sArg += " TRNG_ASK_SEQ="     + quote(AskSeq);
		if (Dialog("form::D07000007P.xml", sArg, 834, 450, "") == true) {		
			if (g_ds_return.rowcount() < 1) return;
			var nRow = -1;
			var RacerNo = "";
			for (var iRow=0;iRow<g_ds_return.rowcount();iRow++) {
				RacerNo = g_ds_return.GetColumn(iRow, "RACER_NO");
				if (dsMentee.FindRow("RACER_NO", RacerNo) == -1) {
					nRow = dsMentee.AddRow();
					dsMentee.SetColumn(nRow, "TRNG_ASK_SEQ", AskSeq);
					dsMentee.SetColumn(nRow, "MENTO_GRP_NO", GrpNo);
					dsMentee.SetColumn(nRow, "MENTO_RACER_NO", Mento);
					dsMentee.SetColumn(nRow, "MENTEE_TRNG_ASK_SEQ", g_ds_return.GetColumn(iRow, "TRNG_ASK_SEQ"));
					dsMentee.SetColumn(nRow, "MENTEE_RACER_NO", g_ds_return.GetColumn(iRow, "RACER_NO"));
					dsMentee.SetColumn(nRow, "MENTEE_RACER_NM", g_ds_return.GetColumn(iRow, "RACER_NM"));										
				}				
			}
		}
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  Çà»èÁ¦ ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
    if (obj.GetForm().id == "divBtnRow") {
		if ( !fnc_DeleteCheck(dsRacerMentoring, "CHK", "1") ) return;
    } else if (obj.GetForm().id == "divBtnRowRacer") {		
		if ( !fnc_DeleteCheck(dsMentee, "CHK", "1") ) return;
    }
}

