	oDataSet          = dsDiaryAcdnt;
	sMandatoryColID   = "RACER_NO|NM_KOR";
	sMandatoryColName = "선수|선수";
	oGrid             = grdDiaryAcdnt;

	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	oDataSet          = dsDiaryDetl;
	sMandatoryColID   = "STR_END_TIME";
	sMandatoryColName = "시작및종료시간";
	oGrid             = grdDiaryDetl;

