>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
    if ( fnc_IsNull(calDt.Value) ) return;

	if ( obj.GetForm().id == "divBtnRowAcdnt" ) {
        dsDiaryAcdnt.AddRow();
        dsDiaryAcdnt.SetColumn(dsDiaryAcdnt.currow, "DT" , calDt.Value);
        dsDiaryAcdnt.SetColumn(dsDiaryAcdnt.currow, "SEQ", dsDiaryAcdnt.Max("SEQ") + 1);
    } else if( obj.GetForm().id == "divBtnRowDetl" ) {
        dsDiaryDetl.AddRow();
        dsDiaryDetl.SetColumn(dsDiaryDetl.currow, "DT" , calDt.Value);
        dsDiaryDetl.SetColumn(dsDiaryDetl.currow, "SEQ", dsDiaryDetl.Max("SEQ") + 1);
    } else if( obj.GetForm().id == "divBtnRowAbs" ) {
        dsDiaryAbs.AddRow();
        dsDiaryAbs.SetColumn(dsDiaryAbs.currow, "DT" , calDt.Value);
        dsDiaryAbs.SetColumn(dsDiaryAbs.currow, "SEQ", dsDiaryAbs.Max("SEQ") + 1);
		dsDiaryAbs.SetColumn(dsDiaryAbs.currow, "ABS_EXC_GBN", "001");

		var j=0;
		for (var i=0; i < dsDiaryAbs.GetRowCount(); i++){
			if(dsDiaryAbs.GetColumn(i, "ABS_EXC_GBN") == "002"){
				j=j+1;
			}
		}

        dsDiaryMst.SetColumn(0, "TOT_PERS_NO" , dsRacer.GetRowCount());
        dsDiaryMst.SetColumn(0, "ABS_PERS_NO" , j);
        dsDiaryMst.SetColumn(0, "EXC_PERS_NO" , dsDiaryAbs.GetRowCount() - j);
        dsDiaryMst.SetColumn(0, "CURR_PERS_NO", dsRacer.GetRowCount() - j);
    }
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	if ( obj.GetForm().id == "divBtnRowAcdnt" ) {
		if ( !fnc_DeleteCheck(dsDiaryAcdnt, "chk", "1") ) return;
	} else if ( obj.GetForm().id == "divBtnRowDetl" ) {
		if ( !fnc_DeleteCheck(dsDiaryDetl, "chk", "1") ) return;
	} else if ( obj.GetForm().id == "divBtnRowAbs" ) {
		if ( !fnc_DeleteCheck(dsDiaryAbs, "chk", "1") ) return;

		var j=0;
		for (var i=0; i < dsDiaryAbs.GetRowCount(); i++){
			if(dsDiaryAbs.GetColumn(i, "ABS_EXC_GBN") == "002"){
				j=j+1;
			}
		}

        dsDiaryMst.SetColumn(0, "TOT_PERS_NO" , dsRacer.GetRowCount());
        dsDiaryMst.SetColumn(0, "ABS_PERS_NO" , j);
        dsDiaryMst.SetColumn(0, "EXC_PERS_NO" , dsDiaryAbs.GetRowCount() - j);
        dsDiaryMst.SetColumn(0, "CURR_PERS_NO", dsRacer.GetRowCount() - j);
    }
}

function calDt_OnChanged(obj,strOldText,strNewText)
{
	fcSearchRacer();
}

function dsDiary_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
    if ( obj.Id == "dsDiaryAbs" ) {
        if ( strColumnID == "NM_KOR" ) {
            if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
                fnc_Message(L_COM_ALT_0016, "N", "선수");
                obj.row = nRow;
                obj.SetColumn(nRow, strColumnID, varOldValue);
                return;
            }
            if ( fnc_GetExistValue(obj, strColumnID, varNewValue, nRow + 1) > 0 ) {
                fnc_Message(L_COM_ALT_0016, "N", "선수");
                obj.row = nRow;
                obj.SetColumn(nRow, strColumnID, varOldValue);
                return;
            }
        }
        else if ( strColumnID == "ABS_EXC_GBN" ) {
        	var j=0;
			for (var i=0; i < dsDiaryAbs.GetRowCount(); i++){
				if(dsDiaryAbs.GetColumn(i, "ABS_EXC_GBN") == "002"){
					j=j+1;
				}
			}

			dsDiaryMst.SetColumn(0, "TOT_PERS_NO" , dsRacer.GetRowCount());
			dsDiaryMst.SetColumn(0, "ABS_PERS_NO" , j);
			dsDiaryMst.SetColumn(0, "EXC_PERS_NO" , dsDiaryAbs.GetRowCount() - j);
			dsDiaryMst.SetColumn(0, "CURR_PERS_NO", dsRacer.GetRowCount() - j);
        }
    }

	if ( strColumnID == "NM_KOR" ) {
        var nRacerRow = fnc_GetExistValue(dsRacer, strColumnID, varNewValue);
        obj.SetColumn(nRow, "RACER_NO", dsRacer.GetColumn(nRacerRow, "RACER_NO"));
	}
}



