function fcSearchRacer2(obj) {

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";

    sServiceName = "d07000004-service:searchDiaryMst";
    sInDataSet   = "";
    sOutDataSet  = "dsDiaryMst=dsDiaryMst "
    	         + "dsDiaryAcdnt=dsDiaryAcdnt "
    	         + "dsDiaryDetl=dsDiaryDetl "
    	         + "dsDiaryAbs=dsDiaryAbs ";

// 수정 필요
	fcd_AddParam("DT", dsRacerTrngMst.GetColumn(dsRacerTrngMst.currow, "DT"));
	fcd_AddParam("TRNG_ASK_SEQ", dsRacerTrngMst.GetColumn(dsRacerTrngMst.currow, "TRNG_ASK_SEQ"));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}


function fcSearchRacerRecode(obj) {
    var sServiceName = "d07000003-service:searchRacerTrngRsltRacer";
    var sInDataSet   = "";
    var sOutDataSet  = " dsRacerTrngRslt=dsRacerTrngRslt ";

	fcd_AddParam("TRNG_ASK_SEQ", dsOutTrngAsk.GetColumn(dsOutTrngAsk.row, "TRNG_ASK_SEQ"));	
	fcd_AddParam("RACER_NO"    , dsOutTrngAskRacer.GetColumn(dsOutTrngAskRacer.currow, "RACER_NO"));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

function fcSearch_NotYet(obj) {
    var sServiceName = "d07000001-service:searchTrngRest";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutTrngRest=dsOutTrngRest ";

	fcd_AddParam(Trim("STR_DT"), '20080604');
	fcd_AddParam(Trim("END_DT"), today());
	fcd_AddParam(Trim("NM_KOR"), dsOutTrngAskRacer.GetColumn(dsOutTrngAskRacer.currow, "NM_KOR"));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function grdDiaryDetl_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (nRow<0) return;
	var sDt = dsRacerTrngMst.GetColumn(dsRacerTrngMst.row, "DT");
    var sTrngAskSeq = dsRacerTrngMst.GetColumn(nRow, "TRNG_ASK_SEQ");
    if ( fnc_IsNull(sDt) ) return;

    var sArg = "Arg_sDt=" + quote(sDt);
    sArg += " Arg_TrngAskSeq=" + quote(sTrngAskSeq);
    Dialog("form::D07000004.xml", sArg, 834, 450, "");
    fcSearch(divSearch.btnSearch);
}

function grdRacerTrngRslt_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    // Content로 넘겨지는 arguements
    var sArg    = " Arg_TrngAskSeq="  + quote(dsOutTrngAsk.GetColumn(dsOutTrngAsk.row, "TRNG_ASK_SEQ"));
        sArg   += " Arg_TrngAskStrDt=" + quote(dsOutTrngAsk.GetColumn(dsOutTrngAsk.row, "TRNG_ASK_STR_DT"));
        sArg   += " Arg_TrngAskEndDt=" + quote(dsOutTrngAsk.GetColumn(dsOutTrngAsk.row, "TRNG_ASK_END_DT"));

    Dialog("form::D07000003.xml", sArg, 250, 250, "");	
}
