/*-------------------------------------------------------------------------------------------------+
>>  신규 자료 생성 여부 체크 및 자료 생성
+-------------------------------------------------------------------------------------------------*/
function checkNewData()
{
	sMeSageCode = SNIS_BOA_F017;
	if ( fnc_Message(sMeSageCode, "Y") ) {
		var arg1 = cboStndYear.Value;
		var arg2 = toString(ToNumber(arg1) -1);
		if ( fnc_Message(SNIS_BOA_F018, "Y", arg1, arg2) ) {
			fcCreateEquip(arg1);
		} else {
			fcCreateEquip(arg2);
		}
	}
}

