/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//??役?자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutEquip )  )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutEquip;
	sMandatoryColID   = "STND_YEAR|EQUIP_TPE_CD|EQUIP_NO";
	sMandatoryColName = "기준년도|장비구분|장비번호";
	oGrid             = grdEquip;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	
	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
	var ServiceName = "e06010010-service:saveEquip";
    var sInDataSet   = "dsOutEquip=dsOutEquip:u";
    var sOutDataSet  = "dsOutEquip=dsOutEquip";
    var equipTpeCd = dsOutEquipTpeCd.GetColumnNF(dsOutEquipTpeCd.rowposition,"CD");
    var sOutDataSet  = "dsOutEquip=dsOutEquip";
    fcd_AddParam("STND_YEAR", cboStndYear.Value);
    fcd_AddParam("MBR_CD", GBL_MBRCD);
	fcd_AddParam("EQUIP_TPE_CD", equipTpeCd);
	fcd_AddParam('USE_YN', cboUseYn.Value);
	
	fcd_SendTransaction(ServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

