/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(ServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	
	if ( ServiceName == "common-service:searchCode" ) {	    
		var fRow = dsOutEquipTpeCd.findRow("CD", "W");
		if (fRow <> -1) 			dsOutEquipTpeCd.DeleteRow(fRow);
		fRow = dsOutEquipTpeCd.findRow("CD", "T");
		if (fRow <> -1) 			dsOutEquipTpeCd.DeleteRow(fRow);
		
		
	} else if ( ServiceName == "e06010010-service:searchEquip" ) { // 조회 후
		var equipTpeCd = dsOutEquipTpeCd.GetColumnNF(dsOutEquipTpeCd.rowposition,"CD");
		if (equipTpeCd == 'M' || equipTpeCd  =='B') {
			if(dsOutEquip.GetRowCount()>0){
				fnc_SetStatus(SNIS_BOA_0003,  dsOutEquip.GetRowCount());
			}else{
				checkNewData();
			}
		}
	}

	// 저장후
	if ( ServiceName == "e06010010-service:saveEquip" ) {
	
	}
	
	//오류가 없이 정상 처리되었다면...
	fcPostProceS(ServiceName);
}

