function fcCreateEquip(stndYear){
	//var stndYear = cboStndYear.Value;
    var equipTpeCd = dsOutEquipTpeCd.GetColumnNF(dsOutEquipTpeCd.rowposition,"CD");
    
    var sEquipSeqCd = equipTpeCd + cboStndYear.Value;
    var sEquipFrNo = 0;
    var sEquipToNo = 0;
    
    // 각 장비별 시작번호 및 종료번호 조회
    var iCnt = dsEquipSeq.FindRow("CD", sEquipSeqCd);
	if (iCnt >= 0) {
		sEquipFrNo = ToNumber(dsEquipSeq.GetColumn(iCnt, "CD_NM"));
		sEquipToNo = ToNumber(dsEquipSeq.GetColumn(iCnt, "RMK"));		
	}
	if (sEquipFrNo <= 0) return;
	grdEquip.Redraw = false;
    for(var i=sEquipFrNo; i<=sEquipToNo; i++){
		dsOutEquip.AddRow();  
		dsOutEquip.SetColumn(dsOutEquip.rowposition,"STND_YEAR",stndYear);
		dsOutEquip.SetColumn(dsOutEquip.rowposition,"MBR_CD",GBL_MBRCD);
		dsOutEquip.SetColumn(dsOutEquip.rowposition,"EQUIP_TPE_CD",equipTpeCd);
		var equipRegNo = fcLPAD(i, 3, '0');
		var equipNo = equipTpeCd+stndYear+equipRegNo;
		dsOutEquip.SetColumn(dsOutEquip.rowposition,"EQUIP_NO",equipNo);
		dsOutEquip.SetColumn(dsOutEquip.rowposition,"EQUIP_REG_NO",equipRegNo);
		dsOutEquip.SetColumn(dsOutEquip.rowposition,"EQUIP_STAT_CD", "001");
		dsOutEquip.SetColumn(dsOutEquip.rowposition,"EQUIP_BUY_DT", CURR_DT);
		dsOutEquip.SetColumn(dsOutEquip.rowposition,"USE_YN", "1");
	}
	grdEquip.Redraw = true;
}

//기준년도, 장비구분에 대한 장비 현황 조회 
function fcSearchEquip(stndYear, mbrCd, equipTpeCd){
	var ServiceName = "e06010010-service:searchEquip";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutEquip=dsOutEquip";
    
    fcd_AddParam("STND_YEAR", stndYear);
    fcd_AddParam("MBR_CD", mbrCd);
	fcd_AddParam("EQUIP_TPE_CD", equipTpeCd);
	//fcd_AddParam('USE_YN', cboUseYn.Value);
	
	
	fcd_SendTransaction(ServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}



//장비구분 row가 변경된경우 function dsOutEquipTpeCd_OnRowPosChanged(obj,nOldRow,nRow)
{
	stndYear = cboStndYear.Value;
	var equipTpeCd = dsOutEquipTpeCd.GetColumnNF(nRow,"CD");
	var equipTpeNm = dsOutEquipTpeCd.GetColumnNF(nRow,"CD_NM");
	labTitle.Text = stndYear +"년 " + equipTpeNm +" 현황";
	 
	 var mbrCd = GBL_MBRCD;
	 
	 //그리드 해더 변경하기 
	 if(equipTpeCd=='M'){
		grdEquip.Format = "Default";
	 }else if(equipTpeCd=='B'){
	 	 grdEquip.Format = "boat_header";
	 }else if(equipTpeCd=='T'){
	 	 grdEquip.Format = "tool_header";
	 }

	 
	if(mbrCd ==  null)mbrCd = CURR_USER_MBR_CD;
	fcSearchEquip(stndYear, mbrCd, equipTpeCd);
}



function fcLPAD(str, len, fillChar){
	str = "" + str;
	var tempStr = "";
	if(str == "")return "";
	var strLength = str.length();
	for(index=0; index< (len-strLength); index++){
		tempStr += fillChar;
	}
	return (tempStr + str);
}

//기준년도가 변경된경우 
function cboStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var equipTpeCd = dsOutEquipTpeCd.GetColumnNF(dsOutEquipTpeCd.rowposition,"CD");
    var equipTpeNm = dsOutEquipTpeCd.GetColumnNF(dsOutEquipTpeCd.rowposition,"CD_NM");
    labTitle.Text = cboStndYear.Value +"년 " + equipTpeNm +" 현황";
    fcSearchEquip(cboStndYear.Value, GBL_MBRCD, equipTpeCd);
}



function dsOutEquip_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID=='EQUIP_NO'){
		dsOutEquip.SetColumn(dsOutEquip.rowposition, 'EQUIP_REG_NO', SubStr(dsOutEquip.getColumn(dsOutEquip.rowposition,'EQUIP_NO'),5,3));
	}else if(strColumnID=='EQUIP_STAT_CD' && varOldValue != varNewValue){
		getSummary();
	}
}

function dsOutEquip_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	getSummary();
}


function getSummary(){
	var totCnt = dsOutEquip.getRowCount();
	var regularCnt = 0;
	var reprCnt = 0;
	var avoidCnt = 0;
	
	for(i=0; i<totCnt; i++){
		var equipStatCd = dsOutEquip.getColumn(i, 'EQUIP_STAT_CD');
		if(equipStatCd=='002'){
			reprCnt++;
		}else if(equipStatCd=='999'){
			avoidCnt++;
		}
	}
	regularCnt = totCnt - (reprCnt + avoidCnt );
	
	edTotCnt.Text = totCnt;
	edReprCnt.Text = reprCnt;
	edRegularCnt.Text = regularCnt;
	edAvoidCnt.Text = avoidCnt;
	
	
}
