//조회 
function fcSearch(obj) {
	fcSearchCheck();
	if (tabTpe.TabIndex == 0) {
		// 공구자산 조회
		fcSearchTools();	
	} else {
		//부품 조회 
		fcSearchParts();
	}
}

//부품  조회 
function fcSearchTools(){
	fcd_AddParam("TOOL_CD", edPartsCd.Text);
    fcd_AddParam("TOOL_NM", edItemCdNm.Text);
    fcd_AddParam("USE_YN",  cboUseYn.Value);       

    var sServiceName = "e06010200-service:searchTools";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutTools=dsOutTools";

	grdTools.Visible = true;
	grdParts.Visible = false;
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

//부품  조회 
function fcSearchParts(){
	fcd_AddParam("PARTS_CD", edPartsCd.Text);
    fcd_AddParam("PARTS_ITEM_CD_NM", edItemCdNm.Text);
    fcd_AddParam("PARTS_TPE", "T");	// 장비구분
    fcd_AddParam("PARTS_GBN", "P");	// 장비구분

    var sServiceName = "e06020010-service:searchParts";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutParts=dsOutParts";

	grdTools.Visible = false;		
	grdParts.Visible = true;
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}




/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if (tabTpe.TabIndex == 0) {
		if ( !fnc_DatasetChangeCheck(dsOutTools ))	{
			//저장 할 자료가 없음
			fnc_Message(SNIS_COM_1006, "N");	
			return false;
		}

		// 필수항목체크
		oDataSet          = dsOutTools;	
		sMandatoryColID   = "TOOL_CD|TOOL_NM";
		sMandatoryColName = "관리번호|품명";
		oGrid             = grdTools;
		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
			return false;
		}
	} else {
		if ( !fnc_DatasetChangeCheck(dsOutParts ))	{
			//저장 할 자료가 없음
			fnc_Message(SNIS_COM_1006, "N");	
			return false;
		}

		// 필수항목체크
		oDataSet          = dsOutParts;	
		sMandatoryColID   = "MODULE_CODE|PARTS_CD|PARTS_COM_CD|PARTS_ITEM_CD_NM";
		sMandatoryColName = "식별번호|품번|업체|품명";
		oGrid             = grdParts;
		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
			return false;
		}
	}

	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
	if (tabTpe.TabIndex == 0) {    
		var sServiceName = "e06010200-service:saveTools";
		var sInDataSet   = "dsOutTools=dsOutTools:u";
		var sOutDataSet  = "dsOutTools=dsOutTools";

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
	} else {
		fcd_AddParam("PARTS_CD", edPartsCd.Text);
		fcd_AddParam("PARTS_ITEM_CD_NM", edItemCdNm.Text);
		fcd_AddParam("MODULE_CODE", edModuleCd.Text);
		fcd_AddParam("PARTS_TPE", "T");	// 장비구분
		fcd_AddParam("PARTS_GBN", "P");	// 장비구분

		var sServiceName = "e06020010-service:saveParts";
		var sInDataSet   = "dsOutParts=dsOutParts:u";
		var sOutDataSet  = "dsOutParts=dsOutParts";

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
	}
}

