	fcd_AddCodeRow(dsOutMbrCd, '전체', '', '');

}

function dsOutParts_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID =="PARTS_CD"){
		var count = 0;
		var parts_code = dsOutParts.GetColumn(nRow, "PARTS_CD");
		for(var i = 1 ; i <= dsOutParts.GetRowCount(); i++) {
			if(parts_code == dsOutParts.GetColumn(i, "PARTS_CD")){
				//alert("2:"+i);
				count = count + 1;
			}
		}

		if(count > 1){
			fnc_SetStatus(L_COM_ALT_0008,  dsOutParts.GetRowCount());		
			dsOutParts.SetColumn(nRow,"PARTS_CD",'');
			return false;	
		}		
	}
}

