//품명스티커 출력 
function btnInspReport_OnClick(obj)
{
	if (tabTpe.TabIndex == 0) {
		//  구현예정
	} else {
		fcPrintSearch();
		fcEquipInspReportPrint();
	}
}

function fcPrintSearch(suppAmt,incomeAmt,inhabAmt) {
	var count1 = 0;
	var count2 = 0;
	var count3 = 0;
	dsOutParts1.ClearData();
	dsOutParts2.ClearData();
	dsOutParts3.ClearData();

	for(var i = 1 ; i <= dsOutParts.GetRowCount(); i++) {
		if( (i % 3) == 1 ){
			dsOutParts1.AddRow(); 
			dsOutParts1.SetColumn(count1,"PRINT_MODULE_CODE",dsOutParts.GetColumn(i-1, "PRINT_MODULE_CODE"));
			dsOutParts1.SetColumn(count1,"PARTS_ITEM_CD_NM", dsOutParts.GetColumn(i-1, "PARTS_ITEM_CD_NM"));			
			dsOutParts1.SetColumn(count1,"PARTS_CD",         dsOutParts.GetColumn(i-1, "PARTS_CD"));			
			count1++;
		} else if ( (i % 3) == 2 ){
			dsOutParts2.AddRow(); 
			dsOutParts2.SetColumn(count2,"PRINT_MODULE_CODE",dsOutParts.GetColumn(i-1, "PRINT_MODULE_CODE"));
			dsOutParts2.SetColumn(count2,"PARTS_ITEM_CD_NM", dsOutParts.GetColumn(i-1, "PARTS_ITEM_CD_NM"));			
			dsOutParts2.SetColumn(count2,"PARTS_CD",         dsOutParts.GetColumn(i-1, "PARTS_CD"));			
			count2++;
		} else {
			dsOutParts3.AddRow(); 
			dsOutParts3.SetColumn(count3,"PRINT_MODULE_CODE",dsOutParts.GetColumn(i-1, "PRINT_MODULE_CODE"));
			dsOutParts3.SetColumn(count3,"PARTS_ITEM_CD_NM", dsOutParts.GetColumn(i-1, "PARTS_ITEM_CD_NM"));			
			dsOutParts3.SetColumn(count3,"PARTS_CD",         dsOutParts.GetColumn(i-1, "PARTS_CD"));			
			count3++;
		}	
	}
	//alert(dsOutParts1.RowCount()+dsOutParts2.RowCount()+dsOutParts3.RowCount());
}

