function fctabOnChanged(obj,nOldIndex,nNewindex)
{
	if (nNewIndex == 0) {
		grdTools.Visible = true;
		grdParts.Visible = false;
	} else {
		grdTools.Visible = false;
		grdParts.Visible = true;
	}
	fcSearch();	
}

function fcgetPartGbnNm() {
	var sPartsTpeNm = "";
	if (tabTpe.TabIndex == 1) {
		sPartsTpeNm = "소모품";
	} else {
		sPartsTpeNm = "부품";
	}
	return sPartsTpeNm;
}



function fcOnChanging(obj,nOldIndex,nNewIndex)
{
	if(nOldIndex == 0) {
		if (dsOutTools.GetUpdate()) {
			if (!fnc_message(SNIS_COM_1036, "Y")) {
				return false;
			}
		}
	} else if(nOldIndex == 1) {
		if (dsOutParts.GetUpdate()) {
			if (!fnc_message(SNIS_COM_1036, "Y")) {
				return false;
			}
		}
	}
}
