	var sServiceName = "e06020020-service:searchPartsCom";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutPartsCom=dsOutPartsCom";
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);    

}

