//부품  조회 
function fcSearchParts(){
	fcd_AddParam("PARTS_CD", edPartsCd.Text);
    fcd_AddParam("PARTS_ITEM_CD_NM", edItemCdNm.Text);
    fcd_AddParam("PARTS_TPE", cboPartsTpe.Value);			// 
    fcd_AddParam("PARTS_GBN", fcgetPartGbn());				// 장비구분

    var sServiceName = "e06020010-service:searchParts";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutParts=dsOutParts";

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}




/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutParts )  )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutParts;	
	sMandatoryColID   = "PARTS_CD|PARTS_COM_CD|PARTS_ITEM_CD_NM";
	sMandatoryColName = "부품코드|업체|품명";
	oGrid             = grdParts;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;

    fcd_AddParam("PARTS_CD", edPartsCd.Text);
    fcd_AddParam("PARTS_ITEM_CD_NM", edItemCdNm.Text);
    fcd_AddParam("PARTS_TPE", cboPartsTpe.Value);			// 
    fcd_AddParam("PARTS_GBN", fcgetPartGbn());				// 장비구분

    var sServiceName = "e06020010-service:saveParts";
    var sInDataSet   = "dsOutParts=dsOutParts:u";
    var sOutDataSet  = "dsOutParts=dsOutParts";

   	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

