/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	if(obj.GetForm().id == 'divPartsButton' ){

		dsOutParts.AddRow();  
		dsOutParts.SetColumn(dsOutParts.rowposition, 'PARTS_TPE', 'M');
		dsOutParts.SetColumn(dsOutParts.rowposition, 'LOC', '창고');
		dsOutParts.SetColumn(dsOutParts.rowposition, 'USE_YN', 'Y');
		dsOutParts.SetColumn(dsOutParts.rowposition, 'PARTS_TPE', fcgetPartInitCd());

	}else{

		dsOutPartsDetail.AddRow();
		dsOutPartsDetail.SetColumn(dsOutPartsDetail.rowposition, 'PARTS_CD', dsOutParts.GetColumn(dsOutParts.rowposition, 'PARTS_CD'));
		dsOutPartsDetail.SetColumn(dsOutPartsDetail.rowposition, 'PARTS_ITEM_CD_NM', dsOutParts.GetColumn(dsOutParts.rowposition, 'PARTS_ITEM_CD_NM'));
		dsOutPartsDetail.SetColumn(dsOutPartsDetail.rowposition, 'SPEC', dsOutParts.GetColumn(dsOutParts.rowposition, 'SPEC'));
		dsOutPartsDetail.SetColumn(dsOutPartsDetail.rowposition, 'PARTS_COM_CD', dsOutParts.GetColumn(dsOutParts.rowposition, 'PARTS_COM_CD'));
		dsOutPartsDetail.SetColumn(dsOutPartsDetail.rowposition, 'COM_NM', dsOutParts.GetColumn(dsOutParts.rowposition, 'COM_NM'));
		dsOutPartsDetail.SetColumn(dsOutPartsDetail.rowposition, 'SAFT_UNIT_CNT', dsOutParts.GetColumn(dsOutParts.rowposition, 'SAFT_UNIT_CNT'));
		dsOutPartsDetail.SetColumn(dsOutPartsDetail.rowposition, 'USE_YN', 'Y');

	}

}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	if(obj.GetForm().id= 'divPartsButton' ){
		if(dsOutParts.GetRowCount()==0){
			fnc_SetStatus(SNIS_COM_1003,  dsOutParts.GetRowCount());		
		}
		dsOutParts.DeleteRow(dsOutParts.Row);

	}else{
		dsOutPartsDetail.DeleteRow(dsOutPartsDetail.rowposition);
	}

}
