***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if ( sServiceName == "common-service:searchCode" ) {
		//var iRow = dsOutPartsTpe.AddRow();
		//dsOutPartsTpe.SetColumn(irow, "CD", "");
		//dsOutPartsTpe.SetColumn(irow, "CD_NM", "전체");
		sPartsTpeOld = "";
		SetFilterPartsTpe();		
    }else if( sServiceName == "e06020010-service:searchParts" ) {
	    if(dsOutParts.GetRowCount()>0){
			btnInspReport.Visible = true;
			fnc_SetStatus(SNIS_BOA_0003,  dsOutParts.GetRowCount());			
	    }else{
			fnc_SetStatus(SNIS_BOA_0003,  dsOutParts.GetRowCount());
	    }	   
	}else if ( sServiceName == "e06020010-service:saveParts" ) {

	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

