	var sArg      = " IMG_URL="+logImgUrl; 
		sArg     += " PARTS_GBN_NM="+fcgetPartGbnNm(); 
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}


function fcgetPartInitCd() {
	var sPartsTpe = "";
	if (tabTpe.TabIndex == 1) {
		sPartsTpe = "W";
	} else {
		sPartsTpe = "M";
	}
	return sPartsTpe;
}


function fctabOnChanged(obj,nOldIndex,nNewindex)
{
	SetFilterPartsTpe();
	fcSearchParts();	
}


function SetFilterPartsTpe()
{
	if (tabTpe.TabIndex == 0) {
		dsOutPartsTpe.filter("CD<>"+quote("W"));
		cboPartsTpe.value = sPartsTpeOld;
	} else {
		sPartsTpeOld = cboPartsTpe.value;
		dsOutPartsTpe.filter("CD="+quote("W"));
		cboPartsTpe.Index = 0;
	}
}
