//그리드 업체 조회 
function fcGrdSearch(){

	var sServiceName = "e06020020-service:searchPartsCom";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutPartsCom=dsOutPartsCom";
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);    

}
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {

   if(!fnc_IsMandatory(calIjrySdt, "주문시작일자")){
		return false;
   }else if(!fnc_IsMandatory(calIjryEdt, "주문종료일자")){
		return false;
   }
   return true;
}

//조회 
function fcSearch(obj) {
	if(!fcSearchCheck()){
		return;
	}

    var sServiceName = "e06020030-service:searchOrder";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutOrderList=dsOutOrderList dsOutOrderPartsList=dsOutOrderPartsList";       

    fcd_AddParam("ORDER_SDT", calIjrySdt.Value);
    fcd_AddParam("ORDER_EDT", calIjryEdt.Value);
    fcd_AddParam("MBR_CD", GBL_MBRCD);
    fcd_AddParam("STND_YEAR", cboStndYear.Value);	
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  주문상세내역 조회
+-------------------------------------------------------------------------------------------------*/
function fcGetSpecCode() {

	var sServiceName = "e06020030-service:searchOrderParts";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutOrderPartsList=dsOutOrderPartsList";

    var order_seq = dsOutOrderList.getColumn(dsOutOrderList.rowposition, "ORDER_SEQ");
    var stnd_year = dsOutOrderList.getColumn(dsOutOrderList.rowposition, "STND_YEAR");      
    fcd_AddParam("MBR_CD", dsOutOrderList.getColumn(dsOutOrderList.rowposition, "MBR_CD"));
	fcd_AddParam("ORDER_SEQ", order_seq);
	fcd_AddParam("STND_YEAR", stnd_year);

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);       
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutOrderList ) )
	{
		if ( !fnc_DatasetChangeCheck(dsOutOrderPartsList )  )
		{//저장 할 자료가 없음
			fnc_Message(SNIS_COM_1006, "N");	
			return false;
		}
	}

	// 필수항목체크
	oDataSet          = dsOutOrderList;
	sMandatoryColID   = "MBR_CD|PARTS_COM_CD|ORDER_DT";
	sMandatoryColName = "경정장|주문업체|주문일자";
	oGrid             = grdOrderList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	grdOrderList.SetFocus();
	for (var iCnt=0;icnt<dsOutOrderPartsList.rowcount();iCnt++) {
		if (toNumber(dsOutOrderPartsList.GetColumn(iCnt, "ORDER_CNT")) < 1) {
			grdOrderPartsList.SetFocus();
			dsOutOrderPartsList.row = iCnt;
			fnc_message("주문하려는 부품의 주문수량은 입력해 주세요!","N");
			return false;
		}
	}
	return true;
}

function fcSaveCheckD(obj) {
	if(edsIndex.Text ==null || edsIndex.Text ==''){
		alert("주문서가 지정되지 않았습니다. 주문서를 지정하세요!");
		return false;
    }
	// 필수항목체크
	oDataSet          = dsOutOrderPartsList;
	sMandatoryColID   = "ORDER_CNT";
	sMandatoryColName = "주문수량";
	oGrid             = grdOrderPartsList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;   
    fcd_AddParam("ORDER_SDT", calIjrySdt.Value);
    fcd_AddParam("ORDER_EDT", calIjryEdt.Value);
    fcd_AddParam("MBR_CD", GBL_MBRCD);
    fcd_AddParam("STND_YEAR", cboStndYear.Value);
    var sServiceName = "e06020030-service:saveOrder";
    var sInDataSet   = "dsOutOrderList=dsOutOrderList:u dsOutOrderPartsList=dsOutOrderPartsList:u";
    var sOutDataSet  = "dsOutOrderList=dsOutOrderList";

   	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}



/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	//GBL_MBRCD = CURR_USER_MBR_CD;
    if(!fnc_IsMandatory(cboStndYear, "기준년도")){
		return false;
    }
    dsOutOrderPartsList.ClearData();
	dsOutOrderList.AddRow();  
	var mbrcd =GBL_MBRCD;
	var year =cboStndYear.Value;
	dsOutOrderList.SetColumn(dsOutOrderList.rowposition,"STND_YEAR",year);
	dsOutOrderList.SetColumn(dsOutOrderList.rowposition,"MBR_CD",mbrcd);
	dsOutOrderList.SetColumn(dsOutOrderList.rowposition,"ORDER_DT",CURR_DT);
	dsOutOrderList.SetColumn(dsOutOrderList.rowposition,"ENT_STATE","001");
	for(var i = 1 ; i <= dsOutOrderList.GetRowCount(); i++) {
		dsOutOrderList.SetColumn(dsOutOrderList.rowposition,"NUM",i);		
	}	

}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	if(dsOutOrderList.GetRowCount()==0){
		fnc_SetStatus(SNIS_COM_1003);		
	}
	if(dsOutOrderList.getColumn(dsOutOrderList.Row, "ENT_STATE") !='001'){
		//fnc_SetStatus(SNIS_BOA_F003);	
	    fnc_Message(SNIS_BOA_F003, "N");	
	}else{
		dsOutOrderList.DeleteRow(dsOutOrderList.Row);
		for(var i = 1 ; i <= dsOutOrderList.GetRowCount(); i++) {
			dsOutOrderList.SetColumn(dsOutOrderList.rowposition,"NUM",i);
		}	
	}

	//if ( !fnc_DeleteCheck(dsOutPartsCom, "num", "1") ) return;
}


