/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "e06020030-service:searchOrder" ) {
		if(dsOutOrderList.GetRowCount()>0){
			if(edsIndex.Text ==null || edsIndex.Text ==''){
				edsIndex.Text = 0;
				//fcGetSpecCode(0);				
			}else{
				//fcGetSpecCode(edsIndex.Text);
			}			
		}else{
			dsOutOrderPartsList.ClearData();
		}


	    if(dsOutOrderPartsList.GetRowCount()>0){
			fnc_SetStatus(SNIS_BOA_0003,  dsOutOrderPartsList.GetRowCount());			
	    }else{
			fnc_SetStatus(SNIS_BOA_0003,  0);
	    }

        edpartsIndex.Text = 0;
	}else if(sServiceName == "e06020030-service:searchOrderParts"){
		if(dsOutOrderPartsList.GetRowCount()>0){
			//fcSetMasterCon();
		}		
	}else if(sServicename == "e06020010-service:searchPartsByOrder") {
		//fcSearch();
	}else if(sServiceName = "e06020030-service:saveOrder") {
		fcSearch();
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}


