function btnParts_OnClick(obj)
{
	fcd_AddParam("STND_YEAR", cboStndYear.Value);	
    fcd_AddParam('MBR_CD', GBL_MBRCD);  
    fcd_AddParam("MODULE_CODE", edModuleCd.Text);
    fcd_AddParam("PARTS_ITEM_CD_NM", edItemCdNm.Text);
    fcd_AddParam('PARTS_COM_CD', dsOutOrderList.GetColumn(dsOutOrderList.rowposition, 'PARTS_COM_CD'));    
    var sServiceName = "e06020010-service:searchPartsByOrder";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutParts=dsOutParts";
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function btnPlus_OnClick(obj)
{
	if(dsOutOrderList.getColumn(dsOutOrderList.Row, "ENT_STATE") !='001'){
		fnc_Message(SNIS_BOA_F003, "N");	
		return;
	}

	if(dsOutOrderList.getColumn(dsOutOrderList.Row, "PARTS_COM_CD") = null){
		fnc_Message(SNIS_BOA_F012, "N");	
		return;
	}

	if(dsOutOrderList.GetRowCount()>0){

		if(edpartsIndex.Text ==''){
			alert("선택된 부품이 없습니다. ");
			return false;
		}	
		if(dsOutOrderList.GetRowCount()==1){
			dsOutOrderPartsList.AddRow();  
			var mbrcd =dsOutOrderList.getColumn(0, "MBR_CD");
			var year =dsOutOrderList.getColumn(0, "STND_YEAR");
			var order_seq =dsOutOrderList.getColumn(0, "ORDER_SEQ");

			var prats_cd = dsOutparts.getColumn(edpartsIndex.Text, "PARTS_CD");
			var prats_year = dsOutparts.getColumn(edpartsIndex.Text, "PARTS_YEAR");
			var prats_item_cd_nm = dsOutparts.getColumn(edpartsIndex.Text, "PARTS_ITEM_CD_NM");
			var spec = dsOutparts.getColumn(edpartsIndex.Text, "SPEC");

			dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"STND_YEAR",year);
			dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"MBR_CD",mbrcd);
			dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"ORDER_SEQ",order_seq);

			dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"PARTS_CD",prats_cd);
			dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"PARTS_YEAR",prats_year);
			dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"PARTS_ITEM_CD_NM",prats_item_cd_nm);
			dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"SPEC",spec);

			dsOutOrderPartsList.SetColumn(edpartsIndex.Text,"ORDER_SEQ",order_seq);

			//품번이 같은게 있음 체크한다.
			var count = 0;
			var parts_code = dsOutOrderPartsList.GetColumn(dsOutOrderPartsList.Row, "PARTS_CD");			
			var parts_year = dsOutOrderPartsList.GetColumn(dsOutOrderPartsList.Row, "PARTS_YEAR");			
			for(var i = 0 ; i <= dsOutOrderPartsList.GetRowCount(); i++) {
				if(parts_code == dsOutOrderPartsList.GetColumn(i, "PARTS_CD") && parts_year == dsOutOrderPartsList.GetColumn(i, "PARTS_YEAR")){
					//alert("2:"+i);
					count = count + 1;
				}
			}

			if(count > 1){
				fnc_SetStatus(L_COM_ALT_0008,  dsOutOrderPartsList.GetRowCount());		
				dsOutOrderPartsList.DeleteRow(dsOutOrderPartsList.Row);
				return false;	
			}		



		}else{
			if(edsIndex.Text ==''){
				alert("선택된 주문서가 없습니다.");
				return false;
			}
			dsOutOrderPartsList.AddRow();  
			var mbrcd =dsOutOrderList.getColumn(edsIndex.Text, "MBR_CD");
			var year =dsOutOrderList.getColumn(edsIndex.Text, "STND_YEAR");
			var order_seq =dsOutOrderList.getColumn(edsIndex.Text, "ORDER_SEQ");

			var prats_cd = dsOutparts.getColumn(edpartsIndex.Text, "PARTS_CD");
			var prats_year = dsOutparts.getColumn(edpartsIndex.Text, "PARTS_YEAR");
			var prats_item_cd_nm = dsOutparts.getColumn(edpartsIndex.Text, "PARTS_ITEM_CD_NM");
			var spec = dsOutparts.getColumn(edpartsIndex.Text, "SPEC");
			dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"STND_YEAR",year);
			dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"MBR_CD",mbrcd);
			dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"ORDER_SEQ",order_seq);

			dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"PARTS_CD",prats_cd);
			dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"PARTS_YEAR",prats_year);
			dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"PARTS_ITEM_CD_NM",prats_item_cd_nm);
			dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"SPEC",spec);

			//품번이 같은게 있음 체크한다.
			var count = 0;
			var parts_code = dsOutOrderPartsList.GetColumn(dsOutOrderPartsList.Row, "PARTS_CD");
			var parts_year = dsOutOrderPartsList.GetColumn(dsOutOrderPartsList.Row, "PARTS_YEAR");			
			for(var i = 0 ; i <= dsOutOrderPartsList.GetRowCount(); i++) {
				if(parts_code == dsOutOrderPartsList.GetColumn(i, "PARTS_CD") && parts_year == dsOutOrderPartsList.GetColumn(i, "PARTS_YEAR")){
					//alert("2:"+i);
					count = count + 1;
				}
			}

			if(count > 1){
				fnc_SetStatus(L_COM_ALT_0008,  dsOutOrderPartsList.GetRowCount());		
				dsOutOrderPartsList.DeleteRow(dsOutOrderPartsList.Row);
				return false;	
			}		
		}

		for(var i = 1 ; i <= dsOutOrderPartsList.GetRowCount(); i++) {
			dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"NUM",i);		
		}
	}
}

