function dsOutOrderPartsList_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == 'ORDER_CNT' and varOldValue != varNewValue){
		genOrderCon();
	}
}


function genOrderCon(){
	var rowCnt = dsOutOrderPartsList.GetRowCount();
	var orcerPartsCnt = 0;
	var orderCon = "";
	var firstPartsNm = "";

	for(i=0; i<rowCnt; i++){
		orcerPartsCnt += dsOutOrderPartsList.GetColumn(i, 'ORDER_CNT');
		if(i==0)firstPartsNm = dsOutOrderPartsList.GetColumn(i, 'PARTS_ITEM_CD_NM');
	}

	if(rowCnt>1){
		orderCon = firstPartsNm + ' ' + '외 ' + (rowCnt-1) +'품목/ ' + orcerPartsCnt +'개';
	}else{
		orderCon = firstPartsNm + ' '  + orcerPartsCnt +'개';
	}
	dsOutOrderList.SetColumn(dsOutOrderList.rowposition, 'ORDER_CON', orderCon);
}

function dsOutOrderPartsList_OnRowDeleted(obj,nRow,nCount)
{
	genOrderCon();
}

function dsOutOrderPartsList_OnRowInserted(obj,nRow,nCount)
{
	genOrderCon();
}

function dsOutOrderList_OnRowPosChanged(obj,nOldRow,nRow)
{
//btnParts_OnClick();
	fcGetSpecCode();
}

function dsOutOrderList_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fcGetSpecCode();
}

