/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {
	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";


	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);


	// 화면 초기화
	fcInit();	


	//년도조회
    fcd_SetStndYear(dsOutStndYear, toInteger(CURR_STNT_YEAR));

   	//주문일자 settting
    var endDt = AddMonth(CURR_DT,-1);
    calIjrySdt.Value = AddMonth(CURR_DT,-1);
    calIjryEdt.Value = CURR_DT;
	http.sync= true;

	//공통코드 조회
	fcd_GetComboDs(dsInComCd);

	http.sync = false;


    fcSearch();		//btnParts_OnClick();
}

