/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {

   if(!fnc_IsMandatory(calIjrySdt, "출고시작일자")){
		return false;
   }else if(!fnc_IsMandatory(calIjryEdt, "출고종료일자")){
		return false;
   }
   return true;

}

//조회 
function fcSearch(obj) {
	if(!fcSearchCheck()){
		return;
	}

    dsOutOrderList.ClearData();
	dsOutOrderPartsList.ClearData();

    var sServiceName = "e06020050-service:searchDelv";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutOrderList=dsOutOrderList";       

    fcd_AddParam("DELV_SDT", calIjrySdt.Value);
    fcd_AddParam("DELV_EDT", calIjryEdt.Value);
    fcd_AddParam("MBR_CD", GBL_MBRCD);
    fcd_AddParam("STND_YEAR", cboStndYear.Value);	
	fcd_AddParam("PARTS_GBN", fcgetPartGbn());	// 장비구분
	fcd_AddParam("PARTS_TPE", cboPartsTpe.Value);	// 장비구분

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

	btnParts_OnClick(); // 불출 가능한 부품리스트도 재조회
}


/*-------------------------------------------------------------------------------------------------+
>>  출고상세내역 조회
+-------------------------------------------------------------------------------------------------*/
function fcGetSpecCode() {

	var sServiceName = "e06020050-service:searchDelvParts";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutOrderPartsList=dsOutOrderPartsList";

    var delv_seq = dsOutOrderList.getColumn(dsOutOrderList.rowposition, "DELV_SEQ");
    var stnd_year = dsOutOrderList.getColumn(dsOutOrderList.rowposition, "STND_YEAR");      

    fcd_AddParam("MBR_CD", GBL_MBRCD);
	fcd_AddParam("DELV_SEQ", delv_seq);
	fcd_AddParam("STND_YEAR", cboStndYear.value);
	fcd_AddParam("PARTS_TPE", cboPartsTpe.value);	// 장비구분     
	fcd_AddParam("PARTS_GBN", fcgetPartGbn());	// 장비구분

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);       
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutOrderList ) )
	{
		if ( !fnc_DatasetChangeCheck(dsOutOrderPartsList )  )
		{//저장 할 자료가 없음
			fnc_Message(SNIS_COM_1006, "N");	
			return false;
		}
	}

	// 필수항목체크
	oDataSet          = dsOutOrderList;
	sMandatoryColID   = "MBR_CD|DELV_DT";
	sMandatoryColName = "경정장|출고일자";
	oGrid             = grdOrderList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSaveCheckD(obj) {

	// 필수항목체크
	oDataSet          = dsOutOrderPartsList;
	sMandatoryColID   = "DELV_CNT";
	sMandatoryColName = "출고수량";
	oGrid             = grdOrderPartsList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}

	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;   
    if ( !fcSaveCheckD() ) return;   
    fcd_AddParam("DELV_SDT", calIjrySdt.Value);
    fcd_AddParam("DELV_EDT", calIjryEdt.Value);
    fcd_AddParam("MBR_CD", GBL_MBRCD);
    fcd_AddParam("STND_YEAR", cboStndYear.Value);
	fcd_AddParam("PARTS_TPE", cboPartsTpe.value);	// 장비구분     
	fcd_AddParam("PARTS_GBN", fcgetPartGbn());		// 장비구분

    http.sync= true;
    var sServiceName = "e06020050-service:saveDelv";
    var sInDataSet   = "dsOutOrderList=dsOutOrderList:u dsOutOrderPartsList=dsOutOrderPartsList:u";
    var sOutDataSet  = "dsOutOrderList=dsOutOrderList dsOutOrderPartsList=dsOutOrderPartsList";

   	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);


   	btnParts_OnClick();
   	http.sync = false;
}



/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	//GBL_MBRCD = CURR_USER_MBR_CD;
    if(!fnc_IsMandatory(cboStndYear, "기준년도")){
		return false;
    }

    dsOutOrderPartsList.ClearData();
	dsOutOrderList.AddRow();  
	var year =cboStndYear.Value;
	dsOutOrderList.SetColumn(dsOutOrderList.rowposition,"STND_YEAR", year);
	dsOutOrderList.SetColumn(dsOutOrderList.rowposition,"MBR_CD",GBL_MBRCD);
	dsOutOrderList.SetColumn(dsOutOrderList.rowposition,"DELV_DT",CURR_DT);
	for(var i = 1 ; i <= dsOutOrderList.GetRowCount(); i++) {
		dsOutOrderList.SetColumn(dsOutOrderList.rowposition,"NUM",i);		
	}	

}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	if(dsOutOrderList.GetRowCount()==0){
		fnc_SetStatus(SNIS_COM_1003);		
	}
	dsOutOrderList.DeleteRow(dsOutOrderList.Row);
	for(var i = 1 ; i <= dsOutOrderList.GetRowCount(); i++) {
		dsOutOrderList.SetColumn(dsOutOrderList.rowposition,"NUM",i);
	}	


	//if ( !fnc_DeleteCheck(dsOutPartsCom, "num", "1") ) return;
}


