function btnPlus_OnClick(obj)
{
	if(dsOutOrderList.GetRowCount()>0){

		// 재고가 0인 경우 처리
		if (toNumber(dsOutParts.getcolumn(dsOutParts.rowposition, "NOW_UNIT_CNT")) <= 0) {
			var sMeSageCode = SNIS_BOA_F015;
			if ( fnc_Message(sMeSageCode, "Y") == false) {
				return false;	
			}
		}

		//품번이 같은게 있음 체크한다.
		var count = 0;
		var sRow = -1;
		var parts_code = dsOutParts.GetColumn(dsOutParts.Row, "PARTS_CD");
		var parts_year = dsOutParts.GetColumn(dsOutParts.Row, "PARTS_YEAR");

		for(var i = 0 ; i <= dsOutOrderPartsList.GetRowCount(); i++) {
			sRow = dsOutOrderPartsList.SearchRow("PARTS_CD=\""+parts_code+"\" && PARTS_YEAR=\"" + parts_year +"\"");
			/*
			if(parts_code == dsOutOrderPartsList.GetColumn(i, "PARTS_CD") && price_stnd_year == dsOutOrderPartsList.GetColumn(i, "PRICE_STND_YEAR")){
				count = count + 1;
			}
			*/
			if (sRow != -1) {
				break;
			}
		}

		if(sRow != -1) {
			fnc_Message(SNIS_BOA_F016);
			dsOutOrderPartsList.row = sRow;
			return false;	
		}		

		dsOutOrderPartsList.AddRow();  
		var mbrcd =dsOutOrderList.getColumn(dsOutOrderList.rowposition, "MBR_CD");
		var year =dsOutOrderList.getColumn(dsOutOrderList.rowposition, "STND_YEAR");
		var delv_seq =dsOutOrderList.getColumn(dsOutOrderList.rowposition, "DELV_SEQ");
		var receptId =dsOutOrderList.getColumn(dsOutOrderList.rowposition, "RECEPT_ID");

		var module_code = dsOutparts.getColumn(dsOutparts.rowposition, "MODULE_CODE");
		var parts_cd = dsOutparts.getColumn(dsOutparts.rowposition, "PARTS_CD");
		var parts_year = dsOutparts.getColumn(dsOutparts.rowposition, "PARTS_YEAR");
		var parts_item_cd_nm = dsOutparts.getColumn(dsOutparts.rowposition, "PARTS_ITEM_CD_NM");
		var spec = dsOutparts.getColumn(dsOutparts.rowposition, "SPEC");
		var nowUnitCnt = dsOutparts.getColumn(dsOutparts.rowposition, "NOW_UNIT_CNT");

		dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"STND_YEAR", year);
		dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"MBR_CD",mbrcd);
		dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"DELV_SEQ",delv_seq);
		dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"RECEPT_ID",receptId);
		dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"ORG_DELV_CNT",0);
		dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"PARTS_CD",parts_cd);
		dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"MODULE_CODE",module_code);

		dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"PARTS_YEAR",parts_year);
		dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"PARTS_ITEM_CD_NM",parts_item_cd_nm);
		dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"SPEC",spec);
		dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"NOW_UNIT_CNT",nowUnitCnt);

	}


}


function btnMinus_OnClick(obj)
{
	if(dsOutOrderPartsList.GetRowCount()==0){
		fnc_SetStatus(SNIS_COM_1003,  dsOutOrderPartsList.GetRowCount());		
	}

	dsOutOrderPartsList.DeleteRow(dsOutOrderPartsList.Row);

	for(var i = 1 ; i <= dsOutOrderPartsList.GetRowCount(); i++) {
		dsOutOrderPartsList.SetColumn(dsOutOrderPartsList.rowposition,"NUM",i);
	}	
}

function dsOutOrderList_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{	
	if(strColumnID =="DELV_DT"){	
		var max = 0;
		var min = 0;
		for(var i = 0 ; i <= dsOutOrderList.GetRowCount(); i++) {
			min = toNumber(dsOutOrderList.GetColumn(i, "DELV_DT"));			
			if(max < min){
				max = min;
			}
		}
		calIjryEdt.Value = toString(max);			
	}
}

function btnOrderPrint_OnClick(obj)
{
	fcPrintSearch();
}


function fcCheckPrint(obj) {
	var delvCnt =0;
    var unitPrice =0;
    var totalSum =0;
	for(var i = 0 ; i <= dsOutOrderPartsList.GetRowCount(); i++) {
		unitPrice += ToInteger(dsOutOrderPartsList.GetColumn(i, "UNIT_PRICE"));
		delvCnt += ToInteger(dsOutOrderPartsList.GetColumn(i, "DELV_CNT"));
		totalSum += ToInteger(dsOutOrderPartsList.GetColumn(i, "AMOUNT"));		
	}

	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e06029050_delv_list.jrf";               //레포트 파일명
	var sDataSet  = "dsOutOrderPartsList=dsOutOrderPartsList";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "STND_YEAR="+dsOutOrderList.getColumn(grdOrderList.clickedRow, "DELV_DT")	;		
		sArg += " IMG_URL="+logImgUrl; 
		sArg += " UNIT_PRICE="+unitPrice; 
		sArg += " DELV_CNT="+delvCnt; 
		sArg += " TOTAL_SUM="+totalSum; 
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);	
}

//부품출고내역서 출력 조회 
function fcPrintSearch(){
	// Content로 넘겨지는 arguements
	var sArg = "Arg_sRacerNm=" ;
	var sResponse = Dialog("form::E06020041.xml", sArg, 834, 450, "");

	if (sResponse==false) return;
	if ( !fnc_IsNull(sResponse) ) {
		edOuta.Text = sResponse[0];
		edOutb.Text = sResponse[1];
		edRecepta.Text = sResponse[2];
		edReceptb.Text = sResponse[3];
	}

	var moter = 0;
	var boat = 0;	
	var moduleCode ="";
	for(var i = 0 ; i <= dsOutOrderPartsList.GetRowCount(); i++) {	
		moduleCode = dsOutOrderPartsList.getColumn(i, "PARTS_TPE");
		if(moduleCode =='M'){
			moter++;
		}else if(moduleCode =='B'){
			boat++;
		}		
	}

	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일??있는 서브디렉토리
	var sRptNm    = "e06029050_delv_check.jrf";               //레포트 파일명
	var sDataSet  = "dsOutOrderPartsList=dsOutOrderPartsList";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "STND_YEAR="+calIjrySdt.Value	;
		sArg += " PARTS_CD="+ replace(dsOutOrderList.getColumn(grdOrderList.clickedRow, "DELV_DESC"), ' ', '@'); 	
		sArg += " OUTPO="+ replace(edOuta.Text, ' ', '@'); 	
		sArg += " OUTNM="+ replace(edOutb.Text, ' ', '@'); 	
		sArg += " RECEPTPO="+ replace(edRecepta.Text, ' ', '@'); 	
		sArg += " RECEPTNM="+ replace(edReceptb.Text, ' ', '@'); 	
		sArg += " MOTER="+moter	; 	
		sArg += " BOAT="+boat; 	

	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);

	fcCheckPrint();

}
//출고수량 등록시
function dsOutOrderPartsList_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID =="DELV_CNT"){
		if(varOldValue != varNewValue)genDelvCon();
		var currCnt = parseFloat(dsOutOrderPartsList.GetColumn(nRow, "NOW_UNIT_CNT"));		
		var delvCnt = parseFloat(dsOutOrderPartsList.GetColumn(nRow, "DELV_CNT"));		

		if(currCnt < delvCnt){
			dsOutOrderPartsList.SetColumn(nRow,"DELV_CNT",varOldValue);	
			fnc_Message(SNIS_BOA_F004,'출고');
		}else{			
		}		
	}
}

function dsOutOrderList_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fcGetSpecCode();
}




function genDelvCon(){
	var rowCnt = dsOutOrderPartsList.GetRowCount();
	var delvPartsCnt = 0;
	var delvDesc = "";
	var firstPartsNm = "";

	for(i=0; i<rowCnt; i++){
		delvPartsCnt += dsOutOrderPartsList.GetColumn(i, 'DELV_CNT');
		if(i==0)firstPartsNm = dsOutOrderPartsList.GetColumn(i, 'PARTS_ITEM_CD_NM');
	}

	if(rowCnt>1){
		delvDesc = firstPartsNm + ' ' + '외 ' + (rowCnt-1) +'품목/ ' + delvPartsCnt +'개';
	}else{
		delvDesc = firstPartsNm + ' '  + delvPartsCnt +'개';
	}

	dsOutOrderList.SetColumn(dsOutOrderList.rowposition, 'DELV_DESC', delvDesc);
}
function dsOutOrderPartsList_OnRowDeleted(obj,nRow,nCount)
{

	genDelvCon();
}

function dsOutOrderPartsList_OnRowInserted(obj,nRow,nCount)
{
	genDelvCon();
}

function dsOutOrderList_OnRowPosChanged(obj,nOldRow,nRow)
{
	fcGetSpecCode();
}

function edItemCdNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == '13' && edItemCdNm.Text != "" ) {
		btnParts_OnClick();
	}
}

function fcgetPartTpe() {
	var sPartsTpe = "";
	if (tabTpe.TabIndex == 1) {
		sPartsTpe = "W";
	} else {
		sPartsTpe = "P";
	}
	return sPartsTpe;
}


function fctabOnChanged(obj,nOldIndex,nNewindex)
{
	SetFilterPartsTpe();
	fcSearch();	
}


function SetFilterPartsTpe()
{
	if (tabTpe.TabIndex == 0) {
		dsOutPartsTpe.filter("CD<>"+quote("W"));
		cboPartsTpe.value = sPartsTpeOld;
	} else {
		sPartsTpeOld = cboPartsTpe.value;
		dsOutPartsTpe.filter("CD="+quote("W"));
		cboPartsTpe.Index = 0;
	}
}

function CHeckCarryYear() 
{
    var iRow = dsLastStndYear.FindRow("PARTS_TPE", fcgetPartTpe());
    if (iRow < 0) {
		// 최종 이월년도 확인 불가 --> 출고 불가
		fnc_Message(SNIS_BOA_F021, "N");
		return false;
    } 
    var sLastStndYear = dsLastStndYear.GetColumn(iRow, "STND_YEAR");
	if (sLastStndYear <> cboStndYear) {
		if (sLastStndYear < cboStndYear) {
			// 아직 이월되지 않음
			fnc_Message(SNIS_BOA_F019, "N");
		} else if (sLastStndYear > cboStndYear) {
			// 이미 이월되었음
			fnc_Message(SNIS_BOA_F020, "N");
		}		
		return false;
	}
}
