//부품  조회 
function fcSearchParts(){
	//fcd_AddParam("STND_YEAR", CURR_STNT_YEAR);
	fcd_AddParam("STND_YEAR", 	cboStndYear.Value);
	fcd_AddParam('MBR_CD', 		GBL_MBRCD);    
    fcd_AddParam("PARTS_GBN", 	fcgetPartGbn());				// 장비구분

    var sServiceName = "e06020060-service:searchParts";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutParts=dsOutParts";

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);   

}

/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e06029060_parts_transfer.jrf";               //레포트 파일명
	var sDataSet  = "dsOutParts=dsOutParts";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "STND_YEAR="+cboStndYear.Value;
		sArg     += " IMG_URL="+logImgUrl; 
		sArg     += " PARTS_GBN_NM="+fcgetPartGbnNm(); 
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}

*-------------------------------------------------------------------------------------------------+
>>  부품을 이월한다.
+-------------------------------------------------------------------------------------------------*/
function btnInspReport_OnClick(obj)
{
	if (obj.id == "btnInspReport") {
		if(fnc_Message(SNIS_BOA_F013, "Y" , cboStndYear.Value, toInteger(cboStndYear.Value) +1, toInteger(cboStndYear.Value) +1) ) {
			fcSavePartsCrfw(obj, "N");   
		}
	} else if (obj.id ==  "btnInspReportCancel") {
		if(fnc_Message(SNIS_BOA_F022, "Y" , cboStndYear.Value, fcgetPartGbnNm())) {
			fcSavePartsCrfw(obj, "Y");   
		}
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  부품을 이월한다.
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {
  if(fnc_Message(SNIS_BOA_F013, "Y" , cboStndYear.Value, toInteger(cboStndYear.Value) +1, toInteger(cboStndYear.Value) +1) ){
 	fcSavePartsCrfw(obj, "N");   
  }
}

function fcSavePartsCrfw(obj, sFlag) {
	//이월 할 자료가 있는지 체크

	if(dsOutParts.GetRowCount()>0){
		if(!fnc_IsMandatory(cboMbrCd, "경정장")){
			return false;
		}

	 	//fcd_AddParam("STND_YEAR", CURR_STNT_YEAR);
		fcd_AddParam("STND_YEAR", dsOutParts.GetColumn(1, "STND_YEAR"));
		fcd_AddParam('MBR_CD', GBL_MBRCD);    
		fcd_AddParam("PARTS_GBN", fcgetPartGbn());	// 장비구분	
		fcd_AddParam("CANCEL_YN", sFlag);	// 장비구분	

		var sServiceName = "e06020060-service:savePartsCrfw";
		var sInDataSet   = "dsOutParts=dsOutParts ";
		//var sOutDataSet  = "dsOutParts=dsOutParts ";
		var sOutDataSet  = "dsLastStndYear=dsLastStndYear ";

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",true);

	}else{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
}

