function fcgetPartGbn() {
	var sPartsTpe = "";
	if (tabTpe.TabIndex == 1) {
		sPartsTpe = "W";
	} else {
		sPartsTpe = "P";
	}
	return sPartsTpe;
}

function fcgetPartGbnNm() {
	var sPartsTpeNm = "";
	if (tabTpe.TabIndex == 1) {
		sPartsTpeNm = "소모품";
	} else {
		sPartsTpeNm = "부품";
	}
	return sPartsTpeNm;
}

function fctabOnChanged(obj,nOldIndex,nNewindex)
{
	SetButton();
	//DisplayLastCarryYear(GetLastCarryYear());
	fcSearchParts();	
}


function SetButton(sCarryYear)
{
    var sLastStndYear = GetLastCarryYear();
	switch (toNumber(sLastStndYear) - toNumber(cboStndYear)) {
		case -1:
			// 당해년도 이월이 완료된 경우 이월버튼을 disable, 이월취소버튼을 enable
			btnInspReport.Enable = true;
			btnInspReportCancel.Enable = false;
			break;
		case 0:
			// 당해년도 이월이 안된 경우 이월버튼을 enable, 이월취소버튼을 disable
			btnInspReport.Enable = true;
			btnInspReportCancel.Enable = true;
			break;
		case 1:
			// 당해년도 이월이 안된 경우 이월버튼을 enable, 이월취소버튼을 disable
			btnInspReport.Enable = false;
			btnInspReportCancel.Enable = true;
			break;
		default:
			// 당해년도 이월이 안된 경우 이월버튼을 enable, 이월취소버튼을 disable
			btnInspReport.Enable = false;
			btnInspReportCancel.Enable = false;
	}
	DisplayLastCarryYear(sLastStndYear);	
}
function GetLastCarryYear()
{
	var sLastCarrayYear = "";
    var iRow = dsLastStndYear.FindRow("PARTS_TPE", fcgetPartGbn());
    if (iRow < 0) {
		// 최종 이월년도 확인 불가 
		fnc_Message(SNIS_BOA_F021, "N");
		return false;
    } 
    sLastCarrayYear = dsLastStndYear.GetColumn(iRow, "STND_YEAR");
    return sLastCarrayYear;
}
function DisplayLastCarryYear(sYear)
{
	if (sYear == "") stLastCarryYear.text = "";
	else stLastCarryYear.text = ToString(ToNumber(sYear)-1)+ " 년";
}


