		return false;
   }else if(!fnc_IsMandatory(calIjryEdt, "출납종료일자")){
		return false;
   }
   /*
   var s = substr(calIjrySdt.Value,0,4);
   var e = substr(calIjryEdt.Value,0,4);
   if( s !=e){
		fnc_Message(SNIS_BOA_F005,"N");
		return false;
   }
   */
   return true;
}

//조회 
function fcSearch(obj) {
	fcSearchCheck();
    //부품 조회 
    fcSearchParts();

}

//부품  조회 
function fcSearchParts(){
	//fcd_AddParam("STND_YEAR", CURR_STNT_YEAR);
	fcd_AddParam("ENT_SDT",    calIjrySdt.Value);    
	fcd_AddParam("ENT_EDT",    calIjryEdt.Value);    
    fcd_AddParam('MBR_CD',     GBL_MBRCD);
    fcd_AddParam('STND_YEAR',  substr(calIjrySdt.Value,0,4));
    fcd_AddParam('STND_MONTH', substr(calIjrySdt.Value,4,2));
    fcd_AddParam('PARTS_CD',   edPartsCd.Text);
    fcd_AddParam('PARTS_NM',   edItemCdNm.Text);
    fcd_AddParam("PARTS_GBN",  fcgetPartGbn());	// 장비구분    

    var sServiceName = "e06030020-service:searchOutPartsDesc";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutParts=dsOutParts";
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}
