/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e06039020_outparts_list.jrf";               //레포트 파일명
	var sDataSet  = "dsOutParts=dsOutParts";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "STND_SYEAR="+calIjrySdt.Value;
		sArg     += " STND_EYEAR="+calIjryEdt.Value; 
		sArg     += " IMG_URL="+logImgUrl; 
		sArg     += " PARTS_GBN_NM="+fcgetPartGbnNm(); 
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}

/*-------------------------------------------------------------------------------------------------+
>>  월계에서 재고 계산
+-------------------------------------------------------------------------------------------------*/
function fc_cal_unit() {
	var sPartsCd = dsOutParts.GetColumn(0, "PARTS_CD");
	var preUnitCnt = 0;

	for( i = 0; i < dsOutParts.GetRowCount(); i++) {
		if(sPartsCd == dsOutParts.GetColumn(i, "PARTS_CD")) {
			if(dsOutParts.GetColumn(i, "SEQ") == 0) {
				preUnitCnt = preUnitCnt + dsOutParts.GetColumn(i, "ENT_CNT") - dsOutParts.GetColumn(i, "DELV_CNT");
			}
			if(dsOutParts.GetColumn(i, "SEQ") == 3) {
				preUnitCnt = preUnitCnt + dsOutParts.GetColumn(i, "ENT_CNT") - dsOutParts.GetColumn(i, "DELV_CNT");
				dsOutParts.SetColumn(i, "NOW_UNIT_CNT", preUnitCnt);
			}
		} else {
			sPartsCd = dsOutParts.GetColumn(i, "PARTS_CD");
			preUnitCnt = 0;

			if(dsOutParts.GetColumn(i, "SEQ") == 0) {
				preUnitCnt = preUnitCnt + dsOutParts.GetColumn(i, "ENT_CNT") - dsOutParts.GetColumn(i, "DELV_CNT");
			}
			if(dsOutParts.GetColumn(i, "SEQ") == 3) {
				preUnitCnt = preUnitCnt + dsOutParts.GetColumn(i, "ENT_CNT") - dsOutParts.GetColumn(i, "DELV_CNT");
				dsOutParts.SetColumn(i, "NOW_UNIT_CNT", preUnitCnt);
			}
		}
	}
}



