/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutParts )  )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}	

	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
    fcd_AddParam("SRCH_DT", calSrchDt.Value);
    fcd_AddParam("MBR_CD", GBL_MBRCD);
    var sServiceName = "e06030050-service:savePartsSight";
    var sInDataSet   = "dsOutParts=dsOutParts:u";
    var sOutDataSet  = "dsOutParts=dsOutParts";

   	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

