/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if ( sServiceName == "common-service:searchCode" ) {

    }else if( sServiceName == "e06030050-service:searchPartsSight" ) {
	    if(dsOutParts.GetRowCount()>0){			
			fnc_SetStatus(SNIS_BOA_0003,  dsOutParts.GetRowCount());			
	    }else{
			fnc_SetStatus(SNIS_BOA_0003,  dsOutParts.GetRowCount());
	    }	


		for(var i=0; i <=dsOutParts.GetRowCount(); i++){
			if(dsOutParts.getColumn(i, "PARTS_CD")=='보트계'){
				edb.Text = i;
			}else if(dsOutParts.getColumn(i, "PARTS_CD")=='모터계'){
				edm.Text = i;
			}else if(dsOutParts.getColumn(i, "PARTS_CD")=='합계'){
				edt.Text = i;
			}		
		} 		 	      	    
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

