//현재고 일괄적용
function btnInspReport_OnClick(obj)
{
	for(var i=0; i <=dsOutParts.GetRowCount(); i++){
		dsOutParts.SetColumn(i,"SIGHT_CNT",dsOutParts.getColumn(i, "NOW_UNIT_CNT"));
	}
}

