}

function dsOutParts_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	if(strColumnID =="SIGHT_CNT" && nRow !=edb.Text && nRow !=edm.Text && nRow !=edt.Text){		
		var nowCnt = parseFloat(dsOutParts.GetColumn(nRow, "NOW_UNIT_CNT"));
		var sightCnt = parseFloat(dsOutParts.GetColumn(nRow, "SIGHT_CNT"));			
		dsOutParts.SetColumn(nRow,"LACK_CNT",sightCnt-nowCnt);			

		var BS =0;
		var MS =0;
		var TS =0;
		var BL =0;
		var ML =0;
		var TL =0;	

		for(var i=0; i < parseFloat(edb.Text); i++){		
			BS = BS+ parseFloat(dsOutParts.GetColumn(i, "SIGHT_CNT"));			
			MS = MS+ parseFloat(dsOutParts.GetColumn(i, "LACK_CNT"));		
		}
		dsOutParts.SetColumn(edb.Text,"SIGHT_CNT",BS);				
		dsOutParts.SetColumn(edb.Text,"LACK_CNT",MS);

		for(var i=parseFloat(edb.Text)+1; i < parseFloat(edm.Text); i++){		
			BL = BL+ parseFloat(dsOutParts.GetColumn(i, "SIGHT_CNT"));			
			ML = ML+ parseFloat(dsOutParts.GetColumn(i, "LACK_CNT"));		
		}			
		dsOutParts.SetColumn(edm.Text,"SIGHT_CNT",BL);				
		dsOutParts.SetColumn(edm.Text,"LACK_CNT",ML);

		dsOutParts.SetColumn(edt.Text,"SIGHT_CNT",BL+BS);				
		dsOutParts.SetColumn(edt.Text,"LACK_CNT",ML+MS);
		return false;		
	}

}

