queryMap.query:
BEGIN
--기존 데이터 삭제 DELETE FROM TBDN_CAND_IDENT WHERE RACER_PERIO_NO = ?; DELETE FROM TBDN_CAND_SCHL_CARR WHERE RACER_PERIO_NO = ?; DELETE FROM TBDN_CAND_FMLY WHERE RACER_PERIO_NO = ?; DELETE FROM TBDN_CAND_JOB_CARR WHERE RACER_PERIO_NO = ?; DELETE FROM TBDN_CAND_CERT WHERE RACER_PERIO_NO = ?; DELETE FROM TBDN_CAND_IDENT_FILE WHERE RACER_PERIO_NO = ?;
INSERT INTO TBDN_CAND_IDENT --후보생신상 ( RACER_PERIO_NO ,CAND_NO ,NM_KOR ,NM_CHN ,BIRTH ,SEX ,PHONE1 ,PHONE2 ,PHONE3 ,CELLPHONE1 ,CELLPHONE2 ,CELLPHONE3 ,EMAIL ,POST_NO ,ADDR1 ,ADDR2 ,PERMNT_POST_NO ,PERMNT1 ,PERMNT2 ,MRRG_YN ,MTARY_GBN ,ARMY_TPE ,BRNCH ,MLT_CLS ,ENTR_ARMY_DD ,LEV_ARMY_DD ,SVCE_TERM ,UNFED_RSN ,HGHT ,WGHT ,SIGHT_LEFT ,SIGHT_RIGHT ,RELGN ,HOBY ,SPA_ABTY ,TH_STAT ,PTO_FILE_VIEW_NM ,PTO_FILE_REAL_NM ,DEL_YN ) SELECT A.RACER_PERIO_NO ,SUBSTR(A.REPT_NO,0,4)||LPAD(ROWNUM,4,'0') ,A.NM_KOR ,A.NM_CHN ,A.BIRTH ,A.SEX ,A.PHONE1 ,A.PHONE2 ,A.PHONE3 ,A.CELLPHONE1 ,A.CELLPHONE2 ,A.CELLPHONE3 ,A.EMAIL ,A.POST_NO1||POST_NO2 ,A.ADDR ,A.DETL_ADDR ,A.PERMNT_POST_NO1||PERMNT_POST_NO2 ,A.PERMNT ,A.DETL_PERMNT ,A.MRRG_YN ,A.MTARY_GBN ,A.ARMY_TPE ,A.BRNCH ,A.MLT_CLS ,A.SVCE_TERM1 ,A.SVCE_TERM2 ,A.SVCE_TERM_MM ,A.SVCE_TERM_BLNG ,A.HGHT ,A.WGHT ,A.SIGHT_LEFT ,A.SIGHT_RIGHT ,A.RELGN ,A.HOBY ,A.SPA_ABTY ,'002' ,A.PTO_FILE ,A.PTO_URL ,A.DEL_YN FROM TBDN_SELT_REPT A, TBDN_SELT_END_RSLT B WHERE A.RACER_PERIO_NO = ? AND NVL(A.DEL_YN,'N') = 'N' AND A.RACER_PERIO_NO = B.RACER_PERIO_NO AND A.REPT_NO = B.REPT_NO AND B.PASS_YN = '001' ORDER BY A.RACER_PERIO_NO ,A.REPT_NO ;
INSERT INTO TBDN_CAND_SCHL_CARR --학력사항 ( RACER_PERIO_NO ,CAND_NO ,SEQ ,LAST_SCHL_NM ,MTRIC_SCHL_YY ,GRDU_SCHL_YY ,MJR ,GRDU_GBN ) SELECT A.RACER_PERIO_NO ,A.REPT_NO ,A.SEQ ,A.LAST_SCHL_NM ,SUBSTR(A.MTRIC_SCHL_DT,1,4) ,SUBSTR(A.GRDU_SCHL_DT,1,4) ,A.MJR ,A.GRDU_GBN FROM TBDN_SELT_SCHL_CARR A, TBDN_SELT_END_RSLT B WHERE A.RACER_PERIO_NO = ? AND A.RACER_PERIO_NO = B.RACER_PERIO_NO AND A.REPT_NO = B.REPT_NO AND B.PASS_YN = '001' ORDER BY A.RACER_PERIO_NO ,A.REPT_NO ;
INSERT INTO TBDN_CAND_FMLY --가족사항 ( RACER_PERIO_NO ,CAND_NO ,SEQ ,FMLY_RELN ,NM_KOR ,JOB_NM ) SELECT A.RACER_PERIO_NO ,A.REPT_NO ,A.SEQ ,A.FMLY_RELN ,A.NM_KOR ,A.OFFC_NM FROM TBDN_SELT_FMLY A, TBDN_SELT_END_RSLT B WHERE A.RACER_PERIO_NO = ? AND A.RACER_PERIO_NO = B.RACER_PERIO_NO AND A.REPT_NO = B.REPT_NO AND B.PASS_YN = '001' ORDER BY A.RACER_PERIO_NO ,A.REPT_NO ;
INSERT INTO TBDN_CAND_JOB_CARR --경력사항 ( RACER_PERIO_NO ,CAND_NO ,SEQ ,STR_DT ,END_DT ,CARR_TERM ,OFFC_NM ,FLOC ,CHA_FLD ,LEAVE_RSN ) SELECT A.RACER_PERIO_NO ,A.REPT_NO ,A.SEQ ,A.STR_DT ,A.END_DT ,A.CARR_TERM ,A.OFFC_NM ,A.FLOC ,A.CHA_FLD ,A.LEAVE_RSN FROM TBDN_SELT_CARR A, TBDN_SELT_END_RSLT B WHERE A.RACER_PERIO_NO = ? AND A.RACER_PERIO_NO = B.RACER_PERIO_NO AND A.REPT_NO = B.REPT_NO AND B.PASS_YN = '001' ORDER BY A.RACER_PERIO_NO ,A.REPT_NO ;
INSERT INTO TBDN_CAND_CERT --자격사항 ( RACER_PERIO_NO ,CAND_NO ,SEQ ,CERT_NM ,CERT_NO ,REG_DT ,CERT_GRD ) SELECT A.RACER_PERIO_NO ,A.REPT_NO ,A.SEQ ,A.CERT_NM ,A.CERT_NO ,A.REG_DT ,A.CERT_GRD FROM TBDN_SELT_CERT A, TBDN_SELT_END_RSLT B WHERE A.RACER_PERIO_NO = ? AND A.RACER_PERIO_NO = B.RACER_PERIO_NO AND A.REPT_NO = B.REPT_NO AND B.PASS_YN = '001' ORDER BY A.RACER_PERIO_NO ,A.REPT_NO ;
INSERT INTO TBDN_CAND_IDENT_FILE --첨부파일 ( RACER_PERIO_NO ,CAND_NO ,FILE_SEQ ,FILE_TITLE ,FILE_NAME ,FILE_URL ,FILE_SIZE ) SELECT A.RACER_PERIO_NO ,A.REPT_NO ,A.FILE_SEQ ,A.FILE_TITLE ,A.FILE_NAME ,A.FILE_URL ,A.FILE_SIZE FROM TBDN_SELT_FILE A, TBDN_SELT_END_RSLT B WHERE A.RACER_PERIO_NO = ? AND A.RACER_PERIO_NO = B.RACER_PERIO_NO AND A.REPT_NO = B.REPT_NO AND B.PASS_YN = '001' ORDER BY A.RACER_PERIO_NO ,A.REPT_NO ;
END;
