mapper.insert:

    		SELECT SEQ_GW_OF_MESSAGE.nextval message_seq FROM dual

		INSERT INTO GW_OF_MESSAGE
		            (
		            	MESSAGE_SEQ, CONTENTS, READ_CNT, CREATE_DT, CREATE_NO, DELETE_YN, SUBJECT
		            )
	     	VALUES (
		     			#{message_seq}, #{contents}, 0, SYSDATE, #{create_no}, 'N', #{subject}
		            )


    		SELECT SEQ_GW_OF_MESSAGE.nextval message_seq FROM dual

		INSERT INTO GW_OF_MESSAGE
		            (
		            	MESSAGE_SEQ, CONTENTS, READ_CNT, CREATE_DT, CREATE_NO, DELETE_YN, SUBJECT
		            )
	     	VALUES (
		     			#{message_seq}, #{contents}, 0, SYSDATE, #{create_no}, 'N', #{subject}
		            )

mapper.select:
SELECT * FROM (SELECT T0.*, FLOOR((ROWNUM - 1) / #{perPageRow,jdbcType=INTEGER} + 1) PAGE FROM (
SELECT * FROM( SELECT GOM.MESSAGE_SEQ, GOM.CONTENTS, TA.READ_CNT, TB.ATTACH_CNT , ORAGMP.F_GET_EMP_KO_NM(GOM.CREATE_NO) EMP_KO_NM, GOM.DELETE_YN, TO_CHAR (GOM.CREATE_DT, 'YYYY-MM-DD HH24:MI:SS') CREATE_DT, GOM.CREATE_NO, GOM.SUBJECT FROM GW_OF_MESSAGE GOM, (SELECT GOMT.MESSAGE_SEQ, SUM(DECODE(READ_YN,'Y',1,0)) READ_CNT FROM GW_OF_MESSAGE_TARGET GOMT GROUP BY GOMT.MESSAGE_SEQ) TA, (SELECT SEQ, COUNT(*) ATTACH_CNT FROM GW_OF_FILE_ATTACH TFA WHERE DELETE_YN = 'N' AND CD = 'O03000' GROUP BY SEQ) TB WHERE DELETE_YN = 'N' AND GOM.CREATE_NO = #{emp_no}
AND TO_CHAR(CREATE_DT,'YYYY-MM-DD') >= #{search_start_ymd} AND TO_CHAR(CREATE_DT,'YYYY-MM-DD') <= #{search_end_ymd}
AND GOM.MESSAGE_SEQ = TA.MESSAGE_SEQ AND GOM.MESSAGE_SEQ = TB.SEQ(+)) TC
WHERE TC.SUBJECT LIKE '%' || #{searchKeyword} || '%'
WHERE TC.EMP_KO_NM LIKE '%' || #{searchKeyword} || '%'
ORDER BY CREATE_DT DESC
) T0 ) WHERE PAGE = #{page,jdbcType=INTEGER}

SELECT * FROM (SELECT T0.*, FLOOR((ROWNUM - 1) / #{perPageRow,jdbcType=INTEGER} + 1) PAGE FROM (
SELECT * FROM( SELECT GOM.MESSAGE_SEQ, GOM.CONTENTS, GOMT.READ_YN, GOM.DELETE_YN, GOM.CREATE_NO, TO_CHAR (GOM.CREATE_DT, 'YYYY-MM-DD HH24:MI:SS') CREATE_DT, TA.ATTACH_CNT, ORAGMP.F_GET_EMP_KO_NM(GOM.CREATE_NO) EMP_KO_NM, GOM.SUBJECT FROM GW_OF_MESSAGE GOM, GW_OF_MESSAGE_TARGET GOMT, (SELECT SEQ, COUNT(*) ATTACH_CNT FROM GW_OF_FILE_ATTACH TFA WHERE DELETE_YN = 'N' AND CD = 'O03000' GROUP BY SEQ) TA WHERE GOMT.DELETE_YN = 'N' AND GOM.DELETE_YN = 'N' AND GOMT.EMP_NO= #{emp_no}
AND TO_CHAR(CREATE_DT,'YYYY-MM-DD') >= #{search_start_ymd} AND TO_CHAR(CREATE_DT,'YYYY-MM-DD') <= #{search_end_ymd}
AND GOM.MESSAGE_SEQ = GOMT.MESSAGE_SEQ AND GOMT.MESSAGE_SEQ = TA.SEQ(+)) TC
read_yn = #{search_read_yn}
AND TC.SUBJECT LIKE '%' || #{searchKeyword} || '%'
AND TC.EMP_KO_NM LIKE '%' || #{searchKeyword} || '%'
ORDER BY READ_YN ,CREATE_DT DESC
) T0 ) WHERE PAGE = #{page,jdbcType=INTEGER}

SELECT MESSAGE_SEQ, SUBJECT, CONTENTS
			, ORAGMP.F_GET_EMP_KO_NM(OM.CREATE_NO) EMP_KO_NM, OM.CREATE_NO
			, TO_CHAR(CREATE_DT,'YYYY-MM-DD HH24:MI:SS') CREATE_DT
  FROM GW_OF_MESSAGE OM
 WHERE MESSAGE_SEQ = #{message_seq} 
     AND DELETE_YN = #{delete_yn}

mapper.update:
UPDATE GW_OF_MESSAGE_TARGET
   SET DELETE_YN = #{delete_yn}
 WHERE MESSAGE_SEQ = #{message_seq}
   AND EMP_NO = #{emp_no}

UPDATE GW_OF_MESSAGE_TARGET
   SET READ_YN = #{read_yn}, READ_DT = SYSDATE
 WHERE EMP_NO = #{emp_no}
   AND MESSAGE_SEQ = #{message_seq}
