mapper.select:
SELECT * FROM (SELECT T0.*, FLOOR((ROWNUM - 1) / #{perPageRow,jdbcType=INTEGER} + 1) PAGE FROM (
SELECT MASTER.APPROVAL_SEQ, MASTER.DOCU_SEQ, MASTER.SUBJECT, MASTER.MAKE_DT, MASTER.STATE, F_GW_GET_CD_NM(MASTER.STATE) STATE_NM, MASTER.REJECTION_REASON, MASTER.SECURITY_YN, MASTER.DECIDE_YN, MASTER.STEP_STATE, MASTER.CREATE_DT, MASTER.CREATE_NO, MASTER.MODIFY_DT, MASTER.MODIFY_NO, MASTER.DELETE_YN, TO_CHAR(MASTER.REQ_DT, 'YYYY-MM-DD') REQ_DT, F_GW_GET_CD_NM(DOCU.DOCU_CD) DOCU_NM, DOCU.DOCU_CD, EMP.EMPNAME AS EMP_KO_NM, ORAGMP.F_GW_GET_LAST_LINE_NM(MASTER.APPROVAL_SEQ) LAST_LINE_NM FROM ORAGMP.CMEMPM EMP, GW_EA_APPROVAL_MASTER MASTER, GW_EA_DOCU DOCU WHERE MASTER.DOCU_SEQ = DOCU.DOCU_SEQ AND MASTER.CREATE_NO = EMP.EMPCODE AND MASTER.DELETE_YN = 'N' AND MASTER.STATE <> 'E02001'
AND TO_CHAR(REQ_DT, 'YYYYMMDD') BETWEEN #{search_start_dt} AND #{search_end_dt}
AND DOCU.DOCU_CD = #{search_docu_cd}
AND MASTER.APPROVAL_SEQ LIKE '%' || #{search_text} || '%'
AND MASTER.SUBJECT LIKE '%' || #{search_text} || '%'
AND EMP.EMPNAME LIKE '%' || #{search_text} || '%'
AND MASTER.STATE = #{search_state}
AND MASTER.MUSTOPINION = 'N'
AND MASTER.MUSTOPINION = 'Y'
ORDER BY MASTER.REQ_DT DESC
) T0 ) WHERE PAGE = #{page,jdbcType=INTEGER}

SELECT COUNT(*) FROM ORAGMP.CMEMPM EMP, GW_EA_APPROVAL_MASTER MASTER, GW_EA_DOCU DOCU WHERE MASTER.DOCU_SEQ = DOCU.DOCU_SEQ AND MASTER.CREATE_NO = EMP.EMPCODE AND MASTER.DELETE_YN = 'N' AND MASTER.STATE <> 'E02001'
AND TO_CHAR(REQ_DT, 'YYYYMMDD') BETWEEN #{search_start_dt} AND #{search_end_dt}
AND DOCU.DOCU_CD = #{search_docu_cd}
AND MASTER.APPROVAL_SEQ LIKE '%' || #{search_text} || '%'
AND MASTER.SUBJECT LIKE '%' || #{search_text} || '%'
AND EMP.EMPNAME LIKE '%' || #{search_text} || '%'
AND MASTER.STATE = #{search_state}
AND MASTER.MUSTOPINION = 'N'
AND MASTER.MUSTOPINION = 'Y'
